insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Таресгоса\nМертва – пожертвовала собой\n\nКалдригос\nМертв – убит дворфами\n\nМадригоса\nМертва – убита Бруталлом','Таресгоса\nМертва – пожертвовала собой\n\nКалдригос\nМертв – убит дворфами\n\nМадригоса\nМертва – убита Бруталлом','233906','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да, мы добыли его последние воспоминания. Они в чешуйке. А еще с нами Травар из Серебряной Длани!','','233913','50401');
