insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','$p и Коранос вернулись. Вы собрали воспоминания Тира?','233919','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Для меня честь – помочь вернуть Тира. Чешуйка сможет его возродить?','','233947','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ужасов слишком много. Они слишком сильны. Мы пойдем с тобой и поможем убить Закажа.','233948','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы будем ждать тебя.','','233949','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ага, кажется, погоня подошла к концу. Прибежал, стоило убить пару врайкулов. Знал бы я раньше...','','233950','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кто-то охраняет это место... Вон тот символ... Это что, Серебряная Длань?','','233958','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы не враги тебе, паладин. Мы пришли сюда с важным заданием. Ради Тира.','','233959','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Проклятые машины! Почему вы меня выкинули?','','233960','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Они вышвырнули меня, заперлись и сломались. Я как раз пытался их починить.','','233962','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Защитные системы гробницы недавно заработали. Почему – не знаю.','','233963','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если от Тира что-нибудь осталось, я это найду. Но мне нужно время.','','233969','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я закрою нас от обломков. $p, эти машины – твоя забота!','','233970','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ага! Думаю, нашел.','','233972','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отлично! $p, выбираемся отсюда, пока вся гробница не обрушилась!','','233973','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надо найти другую панель.','','233975','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Знаю, где есть еще одна. За мной!','','233977','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прости, друг. Нам нужна панель.','','233979','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не уверен. Скоро мы это узнаем.','','233980','50401');
