insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не забывай, что в этом яйце заложено будущее нашего рода. Наше прошлое не властно над спящей внутри малюткой.','233982','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Обращайся с ней хорошо.','233983','50401');
