insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нужно действовать сейчас, иначе все наши замыслы окажутся напрасными. С синими драконами невозможно договориться. Нам придется дать бой.','Нужно действовать сейчас, иначе все наши замыслы окажутся напрасными. С синими драконами невозможно договориться. Нам придется дать бой.','233986','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Драконы отступают! Эта сила спасет Шандарал!','Драконы отступают! Эта сила спасет Шандарал!','233988','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Постойте... Средоточие перегружено! Его не остановить. Оно сейчас взорвется!','Капитан! Заклинание слишком мощное! Его не остановить!','233989','50401');
