insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Любопытно наконец познакомиться с Азурегосом не только по твоим рассказам.','234047','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Он стал бы ценным союзником, если бы сразу пришел на помощь.','','234048','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Так это все ты виноват, Калесгос? Здесь без того полный бардак. Только тебя не хватало, чтобы вконец все испортить.','','234050','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Азурегос! Немедленно замолчи, встреть своего Аспекта с подобающим уважением и доложи о своих трудностях.','234053','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В городе я – лучший торговец изделиями из кожи.','','234054','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Госпожа Синдрагоса?..','','234056','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Приношу извинения. Я не заметил, что стою перед вашей проекцией.','','234057','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я многое слышала о тебе от Калесгоса, юный Азурегос.','234058','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да? И что же наш великий Аспект обо мне говорил?','','234060','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что ты живешь отшельником, идешь всем наперекор и порываешь с любым при малейшей обиде.','','234061','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но как Аспект я положусь на тебя ради будущего синих драконов.','','234062','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я звал тебя на помощь в Лазурном Просторе, но ты не откликнулся. Я звал тебя, когда активировал камень клятвы, но ты и тут промолчал.','','234063','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пусть даже ты не веришь в меня, поверь в свою стаю и помоги нам найти пропавших.','','234066','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Срок вышел, Зерос. Ты думал, что можешь просто улететь?','','234067','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('... Я помогу. Обещаю. Итак. Ты в курсе, что тут происходит?','','234069','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я принес золото! Оно здесь.','','234070','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что тебе нужно?','','234074','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ага... но тут слишком мало. Ты мне должен намного больше.','','234077','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Костоломы! Сдерите чешую!','','234078','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты его не тронешь, барон. Он уплатил долг.','','234079','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Поговорим \"по-деловому\".','','234080','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А-А! Бежим!','','234081','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я буду ждать тебя в детском саду для драконят. Увидимся там!','','234084','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Арти мне платил в три раза меньше. Приказывай, босс!','Арти мне платил в три раза меньше. Приказывай, босс!','234090','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты что меня, купить пытаешься? Я весь твой.','Ты что меня, купить пытаешься? Я вся твоя.','234091','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не проблема. Ты тут теперь хозяин.','Не проблема. Ты тут теперь хозяин.','234092','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я за такие гроши не продаюсь!','Я за такие гроши не продаюсь!','234094','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('За золото спасибо, но контракт не разорву!','За золото спасибо, но контракт не разорву!','234095','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо. А теперь я тебя сдам.','Спасибо. А теперь я тебя сдам.','234096','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись триста восемьдесят шесть. Тени сегодня спокойнее обычного. Я продолжу поиски остальных осколков реликвии.','234098','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('...Прибыльная добыча в течение еще пяти-семи лет...\n\n<Арти опускает перо и оглядывает вас с головы до ног.>\n\nТы кто? Выглядишь так, будто тебе здесь не место.','','234102','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что? Как ты смеешь? Стража! СТРАЖА!\n\n<Арти сердито оглядывается: обычно здесь не так пусто.>\n\nОх... Понятно. Слушай, не трогай меня. Никакое золото не стоит выбитых зубов.\n\nПираты, появившиеся в городе, заплатили мне, чтобы я обесценил кристаллы в \"Пиратском вестнике\". Похоже, их капитану не нравилось, что Зерос так хорошо зарабатывает.\n\nКак зовут капитана? Граб Редкат. Так... По-моему, сейчас их корабль болтается где-то у южного побережья.\n\nНу все, иди. Мне еще нужно закончить важные дела! Торговую компанию лучше не злить.','','234104','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Благодарю тебя. Тени слишком долго провели в мучениях по своей же вине.','234105','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я начну готовить ритуал, который поможет им обрести вечный покой. Буду ждать тебя с осколками.','234106','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кристаллы-шмисталлы! Не попадайтесь на эту удочку!\nОтчет анонимного автора\n\nВолшебные синие кристаллы? Они, конечно же, существуют, но не в Пиратской Бухте. После тщательного осмотра я официально заявляю, что эти кристаллы не ценнее переработанного стекла.\n\nМы сняли все кристаллы с продажи, чтобы сохранить доверие к нашим продавцам. Впрочем, доверие к Зеросу, основному продавцу этих кристаллов, вероятно, уже утрачено. Вам придется поверить мне, когда я раскрою информацию из надежного источника – ради этой инвестиции Зерос взял деньги в долг у самого барона.\n\nБудем надеяться, что Зерос – первая и последняя жертва этой мошеннической схемы.','','234109','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Страницу заполняют синие чернильные завитки.>\n\nСегодня я встретил очень приятную чародейку. У нее большой магический потенциал. Я бы предложил обучить ее, если бы не тяжелые времена. Ее энтузиазм и рвение особенно понравились Эммигосе.\n\nСегодняшний бой с иссохшими завершился очень удачно. Вскоре после него чародейка нас покинула. Похоже, ей было не по себе от наших стычек с иссохшими.\n\nДумаю, дело не только в жалости. Она наверняка считает, что если бы не ее происхождение, она была бы сейчас одной из них.','','234110','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Из-за магических помех в лесу Хрустальной Песни сложно стабилизировать портал, но я прицелюсь поточнее!','','234120','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Желаю удачи. Поклонись от меня Ланигосе и передай, что мы ее очень ждем.','','234121','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Давай подготовим учебный план для новых дракончиков, Лазтак.','','234122','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Будет сделано!','','234123','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я придумал более-менее рабочий метод поиска других синих драконов.','','234133','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Благодарю, Азурегос. Магические вспышки тоже прекратились благодаря нашему другу.','','234134','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Еще раз спасибо, Азурегос. Сообщи мне, если узнаешь что-то новое об этих воспоминаниях. Мне надо приступить к поискам синих драконов.','','234135','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Забавные создания.','','234136','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Красивый выход!','','234137','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Уж я не дам тебе забыть, каких великих жертв ты от меня просишь.','','234138','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я стар... и успел кое-чему научиться за свою жизнь.','','234139','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Азурегос, позволь дать тебе совет.','234140','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тебя больше здесь ничего не держит. Ты вернешься с нами? Мы будем рады, Зерос.','','234141','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Госпожа Синдрагоса... Почту за честь выслушать его.','','234142','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наверное, теперь я у вас в долгу. Я согласен. Надеюсь, острова в жизни не хуже чем в легендах.','','234143','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я знаю, почему ты живешь в уединении в Нексусе. Ты ищешь способ пробить границы меж мирами, чтобы найти возлюбленную.','234144','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Когда открылись врата в Темные Земли, я думал, что у меня появилась надежда... но вопросов по-прежнему больше, чем ответов.','','234145','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты никуда не продвинешься, если будешь работать в одиночку.','234148','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твоя любовь выродится в обиду и ненависть – и к твоей подруге, и ко всему миру, чьи тайны ты не сумел разгадать.','234149','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Попроси о помощи сородичей. Даже если ты не добьешься цели, они утешат тебя.','234151','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Обмануть законы природы и перенести ее в мир живых.','234152','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо, госпожа Синдрагоса. Я запомню твои слова до конца своей жизни.','','234156','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Изломленные духи, которые обитают в этом месте, пережили тысячелетия отчаянной борьбы. Я не могу допустить, чтобы их страдания продолжались и дальше.','234206','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Разбей кристалл, когда в нем скопятся духи, и вместе с ним разобьется проклятие, приковывающее их к этим руинам.','234229','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тени постепенно рассеются... и руины опустеют. Духи Шандарала наконец обретут долгожданный покой.','234250','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Стало ясно, что мы – и я, и Аспекты – знаем о Тире меньше, чем думали. Теперь его пробуждение – первостепенная задача.','','234298','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я зачаровала новый кристалл-средоточие. Активируй его возле источника чародейской энергии, и духи слетятся к нему сами. Большое хрустальное дерево подойдет.','234311','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чудовище, которое напало на Стражей, может быть еще поблизости. Держите ухо востро.','','234314','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо, Бликрез. Пошли, $p.','','234328','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','После стольких лет... Осколков наконец хватит, чтобы довершить дело.','234330','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пора вспомнить все, чему меня учили.','','234349','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой! Мне страшно!','','234358','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Теперь это наш корабль!','Теперь это наш корабль!','234359','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты не из наших!','Ты не из наших!','234360','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Захватчик! Бросить за борт!','Захватчик! Бросить за борт!','234361','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ухожу... в пучину...','Ухожу... в пучину...','234362','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да пожрет тебя Щуплий...','Да пожрет тебя Щуплий...','234363','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ага! Подбираешься к моему сокровищу, гад ползучий?','','234364','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да укроет меня Великое море...','','234365','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Во имя Света! Ты и вправду творение Тира.','','234373','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пи-пик! Пи-пик! Пи-пик!','','234378','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я открою портал к Азурегосу в Тайный склеп. Помогай ему во всем и выясни, с чем мы столкнулись на этот раз.','','234382','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Лазурные архивы станут новым домом для синих драконов – и все благодаря тебе.','','234386','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мм-м, драконов фрукт...','','234388','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надеюсь, останки Тира здесь. Это будет... непросто.','','234390','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А ты прав. Здесь... здесь сила... Я тоже его чувствую!','','234392','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вовремя. Гробница начала обваливаться. Должно быть, держится только на силе Тира. Хитро.','','234393','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Големы явно что-то охраняют. Иначе бы их здесь не было. Мы уже близко.','','234394','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тир здесь... его сущность здесь. Я чувствую.','','234395','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я почти закончил.','','234399','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('О! Можно выбрать? Ух ты!','','234414','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Постойте! Попробуй эту пару!','234432','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твоя догадка оправдалась, Азурегос. А вот и посланник от Калесгоса.','234462','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пока мы храним воспоминания Тира, на нас никто не станет нападать. Мы в безопасности.','','234494','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','...Когда я увидела, что мы причиняем боль другим драконидам, я поняла, что выбрала не ту сторону.','234517','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Но что заставило тебя присоединиться к ним? Ты не кажешься бесчестной.','234518','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Напротив, именно честь заставила меня присоединиться к ним. Отношение к драконидам было... бесчестным.','234519','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы заботились об этой земле на протяжении веков, пока драконов здесь не было, но, когда вы вернулись, нас низвели до уровня простых слуг.','234520','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вот почему я присоединилась к мятежникам – потому что нас оттеснили в сторону, ожидая, что мы будем служить драконам так, будто они никуда не уходили.','234521','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отлично, все в сборе. Чем нас больше, тем легче мы справимся с магией, пробившейся из гробницы!','','234540','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Дракончик с восхищением слушает рассказ.>','<Дракончик с восхищением слушает рассказ.>','234595','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чур ты водишь!','Чур ты водишь!','234596','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не умею читать.','Я не умею читать.','234597','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не топочи так. Испугаешь их лидера.','234620','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У тебя слишком мягкое сердце. Было и есть. Эти маги опасны, и церемониться с ними нельзя.','','234621','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('На их башнях флаг Терамора! Они скорбят. Они боятся за свою жизнь. Я верю, что с ними можно договориться.','','234623','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как думаешь, а здесь меня найдут, маленький Жуса?','Как думаешь, а здесь меня найдут, маленький Жуса?','234637','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ха! Это укрытие самое лучшее!','Ха! Это укрытие самое лучшее!','234640','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Апчхи!','','234644','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой... Ай... Сейчас чихну...','','234646','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я тоже хочу путешествовать и находить интересные штуки!','Я тоже хочу путешествовать и находить интересные штуки!','234670','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если зайдешь в пещеру, ты обязательно найдешь медведя и на тебя нападет стая летучих мышей.','Если зайдешь в пещеру, ты обязательно найдешь медведя и на тебя нападет стая летучих мышей.','234671','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Дракончик смотрит на ваше оружие, выпучив глаза.>\n\nКогда я вырасту, я тоже найду себе такое оружие!','<Дракончик смотрит на ваше оружие, выпучив глаза.>\n\nКогда я вырасту, я тоже найду себе такое оружие!','234672','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если я увижу гнолла, то ткну его палкой.\n\n<Дракончик кивает со знающим видом.>','Если я увижу гнолла, то ткну его палкой.\n\n<Дракончик кивает со знающим видом.>','234673','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Под вулканами есть специальные трубы, через которые проходит лава.','Под вулканами есть специальные трубы, через которые проходит лава.','234674','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гага-пан сказал, что если я буду хорошо слушать, то смогу поесть конфет!','Гага-пан сказал, что если я буду хорошо слушать, то смогу поесть конфет!','234675','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бла-бла-бла...','Бла-бла-бла...','234676','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тебе твои передние зубы очень дороги?','Тебе твои передние зубы очень дороги?','234677','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В некоторых камнях можно найти много золота и серебра!','В некоторых камнях можно найти много золота и серебра!','234678','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Дракончик смотрит вдаль, бормоча себе под нос.>\n\nВернутся ли они домой к обеду?','<Дракончик смотрит вдаль, бормоча себе под нос.>\n\nВернутся ли они домой к обеду?','234679','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сколько бобров поместится у тебя в карманах?','Сколько бобров поместится у тебя в карманах?','234680','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Дракончик с серьезным видом смотрит на вас.>\n\nЕсли принесешь мне молочный коктейль, я дам тебе целых три картофелины.','<Дракончик с серьезным видом смотрит на вас.>\n\nЕсли принесешь мне молочный коктейль, я дам тебе целых три картофелины.','234681','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слышишь? Птицы поют.','','234695','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Кейлек... как я рада.','234709','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Столько лет прошло.','','234710','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Долго же ты собирался меня навестить.','234711','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Обязанности Аспекта отнимают все больше времени. Прости, Кири. Мне стоило прийти раньше.','','234712','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хоть наша деревня и небольшая, мы очень гордимся своим ремеслом и дорожим отношениями друг с другом.','234717','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Здравствуй, $p. Я бы предложил напитки, но обстоятельства не позволяют. \n\nМы собирались осмотреть башню на севере, но по пути на нас напали джарадины, и нам пришлось спешно отступать. Мы очень утомлены, потеряли одного из товарищей и серьезно пострадали от ран.\n\nНе согласишься ли ты нам помочь?','','234771','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Блуждающие сущности Синдрагосы и Малигоса кричат, умоляя о покое. Мы должны найти способ дать его им.','','234777','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Скорее! Калесгос и Синдрагоса могут знать, как нам поступить.','','234784','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты прав. Очень странно смотреть на свои останки со стороны. Я слышу ее крики... она зовет меня.','234785','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо Азурегосу и всем остальным. Я думаю, мы выяснили причину.','','234786','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я уже очень давно жду этого. \n\nСиние драконы снова вместе. Наследие Синдрагосы и Малигоса нашло покой, и дети моих детей снова могут наслаждаться красотой Драконьих островов.\n\nПора и мне уйти на покой, $n. Меня ждут многие драконы... включая тех, по кому я тосковал каждый день своей долгой жизни.','','234852','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я не понимаю, зачем нам драконы! Они оставили эту землю нам на 10 000 лет, и все было прекрасно.','234862','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Снова выбираешь милосердие? Странно, что тебя даже встреча с Рашагет не исцелила от этой опасной болезни.','','234889','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Маги гибли всякий раз, когда Малигос пытался развязать войну со смертными. И к чему нас это привело? Я разорву этот кровавый порочный круг.','','234890','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да ну? Так вперед. Только не прибегай потом к нам, чтобы мы за тобой все разгребали.','','234891','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты лидер синих драконов. Мы подчинимся твоему решению. И если нам очень повезет, твой \"мирный договор\" не завалит трупами драконов.','234892','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эта печать смерти смягчит удары, чтобы они только усмиряли магов, а не убивали. Приходи на пристань, когда закончишь.','','234893','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я не думала об этом. Но они не могут не понимать, что защитить острова можно лишь совместными усилиями.','234923','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Каждый раз когда случается война, драконы отправляют нас умирать – в огромных количествах. Они хотят, чтобы мы сражались за них, но что мы получаем взамен?','234930','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Столько драконов погибло, защищая Азерот на протяжении веков. Я всегда считала, что мы сражаемся вместе, а не посылаем своих бойцов на войну.','234931','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы не просились на службу к драконам. Мы не выбирали службу драконам. Нам просто приказали – вопреки нашей воле.','','234934','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нам нужны были добровольные союзники, а не рабы. Похоже, мы принимали их преданность как должное.','234935','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ах! Я их ждал. Эти цветы уже зрелые.\n\nВзгляни, какие синие... Таким будет напиток, о котором будут говорить пандарены со всех уголков земли.','','234936','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','К слову об изысканных нотках! Вон те цветы Киригосы – просто находка для моего ремесла. Когда я смешиваю их с другими ингредиентами, они усиливают эффект готового зелья.\n\nА какие они красивые!','234937','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Киригоса? Это ты?\n\nА, ты ее $gдруг:подруга;! Она обещала навестить меня сегодня... Цветы у тебя?\n\n<Старик Туманный Глаз прижимает к себе цветы и глубоко вдыхает их аромат. На его лице читается неподдельное удовольствие.>\n\nЯ уже почти не вижу, поэтому стараюсь баловать себя тем, что еще могу ощутить. А цветы, которые выращивает Киригоса, это что-то с чем-то.\n\nДобро пожаловать в мое скромное жилище, $gдруг:подруга;.','','234938','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Цветы даже красивее, чем я могла представить! Только взгляни на эти бархатистые лепестки – они будто мерцают волшебством. Магия Киригосы облагораживает нашу деревню.\n\nЯ как раз подыскивала новую приправу для моей лапши. И, думаю, несколько цветков можно поставить в вазу...','234939','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я уверен, что ты можешь найти какой-нибудь древний артефакт, который подарит мне еще одну тысячу лет или обратит время вспять. Уверен, потому что и сам находил такие! Как еще, по-твоему, я продержался столько лет? \n\nКогда моя дочь, мать Стеллагосы, умирала, я дал ей слово. Обещал, что буду присматривать за Стеллагосой и остальными, пока наш род не возобновится.\n\nЯ искусственно продлевал себе жизнь разными способами. Я уже давно должен был умереть... но не мог оставить семью в одиночестве.\n\nМеня не нужно \"спасать\", $n. Мое время давно вышло. Я наконец-то уйду на покой – и я принимаю это.','','234941','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вальдраккен был нашим домом, но драконы вытеснили нас, когда вернулись.','234951','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я... не верю, что больше никогда его не увижу. Что же нам теперь делать?','234952','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нашу стаю объединит память о наших предшественниках. О Сенегосе, Синдрагосе, Малигосе... обо всех, кого уже нет с нами.','','234953','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы можете оставаться в архивах, сколько пожелаете. Они станут нам новым домом.','','234954','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Здесь попросторнее, чем в Нексусе. А в хранилищах полно книг, которые надо исследовать.','','234955','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я помогу тебе в Нексусе, чтобы ты мог сюда приходить.','234956','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я тоже! У старины Малигоса наверняка найдется пара идей, куда вложить деньги.','','234957','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','А давайте... Зерос не будет один ходить в Нексус.','234958','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я буду изучать хранилища с тобой, Азурегос. Есть у меня несколько гипотез...','234959','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Можно и вместе для разнообразия. А то все один да один.','','234960','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если я вам понадоблюсь, можете найти меня тут.','','234961','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы всегда считали Вальдраккен своим домом. Мы должны помнить, что это дом и для драконидов.','234962','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У меня есть свои мечты – я не хочу лишь воевать и умирать за драконов.','234963','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я не думала об этом. Я не хочу мешать им претворять свои мечты в жизнь.','234964','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо за помощь. Мы с тобой покажем им, что для синих драконов есть другой путь.','','234967','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Калесгос вздыхает.','','234968','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я наметила наш дальнейший план, хотя без риска не обойтись.','234969','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как думаешь, $n? Претензии драконидов оправданы?','234970','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, твоя точка зрения мне понятна. А что ты думаешь об их желании быть равными драконам?','234974','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо за твое мнение. Как, по-твоему, было бы правильно разрешить ситуацию с мятежниками?','234978','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я приму твой совет к сведению. Спасибо за помощь, $n.','234982','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Благодарю за твою помощь в городе. Надеюсь, тебе понравились мои друзья.','234986','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Недалеко отсюда лидер Растоптанного Знамени.','','234987','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('По словам Суригосы, она сильный маг. Я пойду с тобой на всякий случай, но...','','234988','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет, Калесгос. Пусть идет в одиночку.','234989','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Наше появление только осложнит переговоры. Она испугается нападения. Мы разрываем круг или нет?','234992','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('...справедливо. Пусть будет по-твоему. Мы подождем тут. Останови их лидера, пока она не натворила непоправимого.','','234993','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Так ты почитаешь мне или нет? И в этот раз выбери сказку получше!','Так ты почитаешь мне или нет? И в этот раз выбери сказку получше!','234999','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А ты знаешь сказку про акулу? Обожаю истории про акул. То есть именно ту историю. Ты-то мне вряд ли хорошую расскажешь...','А ты знаешь сказку про акулу? Обожаю истории про акул. То есть именно ту историю. Ты-то мне вряд ли хорошую расскажешь...','235001','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Воспитатель говорит, что я читаю сказки лучше всех. Я все время читаю. Ты тоже побольше читай!','Воспитатель говорит, что я читаю сказки лучше всех. Я все время читаю. Ты тоже побольше читай!','235002','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это другие дракончики ничего не понимают без картинок, а я и без них могу. Глупая книга!','Это другие дракончики ничего не понимают без картинок, а я и без них могу. Глупая книга!','235003','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Долго еще? Какая скукотища!','Долго еще? Какая скукотища!','235004','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Давай забавным голосом! Ну же!','Давай забавным голосом! Ну же!','235005','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У-у-у... Мы эту уже сто миллионов раз слушали...','У-у-у... Мы эту уже сто миллионов раз слушали...','235006','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Почему картинки такие тусклые? Не было зачарованной книги получше?','Почему картинки такие тусклые? Не было зачарованной книги получше?','235007','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой! У этой сказки грустный конец. Мне так воспитатель сказал!','Ой! У этой сказки грустный конец. Мне так воспитатель сказал!','235008','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Почему в каждой истории должны быть драки?','Почему в каждой истории должны быть драки?','235009','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('О... Уже все?','О... Уже все?','235010','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эта вроде ничего...','Эта вроде ничего...','235011','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хочу немного вздремнуть...','Хочу немного вздремнуть...','235012','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расскажи потом про акулу! Ну пожа-а-алуйста!','Расскажи потом про акулу! Ну пожа-а-алуйста!','235013','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А давай теперь перекусим!','А давай теперь перекусим!','235014','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твое возвращение растревожило старые раны, Калесгос. А острова... я мечтаю вернуться, поверь.','235023','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если ты говоришь правду и синие драконы снова едины... я не смела и надеяться.','235024','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это правда. Прежде всего ты синий дракон, Кири. Твой дом с нами.','','235025','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пандария совсем не похожа на нашу родину, и все же здесь я обрела ее впервые за много лет.','235026','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Завела новую семью. Будь у меня выбор, я бы ни за что их не оставила.','235027','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кири... Прости. У тебя есть выбор.','','235028','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мне надо все обдумать и принять решение.','235029','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не уходите, полюбуйтесь пока пейзажем.','235030','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я скоро вернусь.','235031','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я закончу работу ради всех павших! Любой ценой!','235041','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет! Я не брошу на полпути. От меня зависит множество жизней!','235043','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Значит, пришли надо мной посмеяться? Или раздавить как муравья?','235044','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы хотим знать, чего ты добиваешься. Зачем воссоздавать такое страшное оружие?','235045','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','А какой у нас был выбор? Нам пришлось защищаться. Я ничего не смогла поделать с первой мана-бомбой! И потому Ронин и Киннди... все они умерли!','235046','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вы клялись нас защищать. Вы должны были не допустить такого! Но в итоге так и не сумели. И наш дом теперь уничтожен. Мы потеряли его навсегда.','235047','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Суригоса хранит молчание.','235048','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('... Тури Вихресверк? Я тебя помню. Ты одна из учениц Ронина.','','235050','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Все это правда. Мы пытались защитить Радужное Средоточие. Мы пытались защитить Терамор и его жителей. Но не смогли.','','235051','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Никакие мои слова не вернут павших. Никакими словами не описать, как я жалею об этой трагедии. Но так нельзя.','','235052','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы справимся вместе, Тури. Мы не повторим ошибок наших предшественников.','','235053','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Просто... Я так по ним скучаю...','235056','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я знаю. Но ты можешь пойти с нами. Я внимательно выслушаю, как ты предлагаешь устроить жизнь своим союзникам. Как ученик Ронина, я очень ценю твое мнение.','','235058','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Суригоса молча наблюдает. Она смотрит на вас, а потом кивает в сторону портала.','235060','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','От пороха нет никакого прока, если не знаешь, когда рванет. Слишком поздно – не заденет врага. Слишком рано – заденет тебя.','235072','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Терамор... Только этого не хватало.','','235085','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В записях Азурегоса говорится, что он собирался искать Суригосу сам. Если положение настолько сложное, я лучше отправлюсь с тобой.','','235086','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Будет разумно не подходить слишком близко, пока не осмотримся. Этот портал ведет на скалы сразу за Терамором.','','235087','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нет, не то... Может, эта?','','235091','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ага! Вот она.','','235092','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я точно читал эту книгу... но когда?','Я точно читала эту книгу... но когда?','235093','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вернемся к капитану Драйн – она у входа в бастион. Я хотела бы обсудить слова мятежников.','235095','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('\"Пьяный хозен\"? Чудесно. Встретимся там.','','235110','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сюда, $p!','','235113','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я рад за Кири. Она заслуживает только лучшего.','','235116','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Она посвятила всю жизнь служению синим драконам.','','235117','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Даже после... после потери... после...','','235118','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Несчастья, какого я не пожелаю злейшему врагу.','','235119','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Она уже потеряла самое дорогое. На островах она получит все, о чем мечтала.','','235120','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пей до дна, $p. За процветание синих драконов.','','235121','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хотел бы я, чтобы все сложилось иначе. Но если здесь Кири по-настоящему счастлива, то я не смею желать большего.\n\nСпасибо тебе за компанию. Пора возвращаться в архивы.','','235136','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Может, здесь подремать?','','235139','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ух... Тут неудобно...','','235140','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тсс! У нас тайная операция!','Тсс! У нас тайная операция!','235142','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('ZzzzzzZZZzzz...','ZzzzzzZZZzzz...','235143','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Фу!','','235145','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('$p! Эх, не получилось спрятаться, но мы так разговорились...','$p! Эх, не получилось спрятаться, но мы так разговорились...','235146','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ух ты! Здорово!','Ух ты! Здорово!','235147','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вот это место! Надо будет сюда вернуться!','Вот это место! Надо будет сюда вернуться!','235148','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это ты! Мне надо поучиться прятаться...','','235149','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А ты точно любишь кофе, а не чай?','А ты точно любишь кофе, а не чай?','235150','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пленных мятежников можно не бояться – их охраняют чешуекляты.','235177','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Дорогой мой Кейлек. Мне казалось, я никогда не привыкну к горю.','235193','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо, $n. Благодаря тебе я стала лучше понимать мятежников. И очень хорошо, что Белафон здесь.','235194','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Белафон, восстание длится уже достаточно долго. И уже достаточно долго дракониды терпят наше неуважительное отношение.','235196','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я попросила |3-3($n) привести тебя к Престолу Аспектов, чтобы обсудить права и справедливое отношение, которых заслуживают дракониды.','235197','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как я рада, что вы образумились! Мы долго и упорно сражались за...','235198','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Белафон! Это решение было принято вопреки твоей жестокости, а не благодаря ей.','235199','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Многие дракониды, в том числе твои мятежники, мертвы из-за этого восстания. Многие были невинны. И никто не должен был умирать.','235202','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Разговаривая с пленными мятежниками, я осознала, что порой мы относились к драконидам неуважительно, пренебрежительно и несправедливо.','235203','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы, драконы, должны придерживаться более высоких стандартов – и начнем мы с того, что будем относиться к драконидам, как к равным.','235204','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','За это я и боролась.','235205','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Но ты все же должна ответить за свои преступления.','235206','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я все делала для того, чтобы об участи драконидов узнали все!','235207','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','И сколькими сородичами ты пожертвовала ради этого?','235208','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Однако, насколько я понимаю, ты – мастер слова и пера.','235209','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, это действительно так.','235210','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Отныне и впредь мы будем предлагать драконидам присоединиться к нашей работе, а не требовать этого. С уважением, как членам нашей семьи, а не простым слугам.','235211','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если ты готова пустить в дело слова и перо, чтобы описать новые реалии и это торжественное предложение защищать Азерот вместе...','235212','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, я сделаю это для своего народа.','235213','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тогда я отсрочу твое наказание – на время, пока ты выполняешь эту работу.','235214','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пока драконы соблюдают этот договор, я тоже буду его соблюдать.','235215','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мальдра, во время этой борьбы твоя помощь была неоценима.','235232','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Готова ли ты принять мое предложение? Служить вместе с драконами, чтобы защищать эти острова и Азерот?','235233','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Моя королева, я польщена твоей просьбой, учитывая мою помощь мятежникам.','235234','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я подумаю над твоим предложением, но пока что откажусь. Потребуется время, чтобы узнать, будут ли драконы поддерживать эти равноправные отношения.','235235','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если я увижу, что на островах с драконидами и правда обращаются справедливо, возможно, я вернусь и предложу свою помощь.','235236','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Изменить свое поведение гораздо труднее, чем обещать это на словах.','235237','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Понимаю. Жаль это слышать, но у тебя есть полное право сделать такой выбор.','235238','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если ты передумаешь, мы всегда будем рады твоей помощи.','235239','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Учитель Ронин... Киннди... Простите меня.','235241','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прости, моя королева! Срочное дело вынуждает меня прервать тебя.','','235243','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Следователь Эрима? Что ты хочешь доложить?','235244','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как бы мы ни разрешили ситуацию с мятежниками, полагаю, нам придется изменить свое отношение к драконидам.','235252','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И вот мы снова оказались в крайне необычном месте, $n.\n\nНо, думается мне, все не зря. Предвижу, что жизнь сильно изменится, когда мы раскроем секреты Нелтариона.','','235266','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Для меня целью восстания всегда было справедливое отношение к драконидам. Я никогда не выступала против самих Аспектов!','235270','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо за помощь, друг мой – и тебе, Суригоса. Не знаю, что бы мы делали без таких трудолюбивых союзников.','','235395','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы трое возвращайтесь на острова. Я сразу за вами – сначала поговорю с Тури.','','235396','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Признаю, Калесгос: не ожидал, что твой план сработает. Но ты очень живо со всем разобрался.','','235397','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нет... Пока нет. Работы еще много.','','235398','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Суригоса вздыхает.','235399','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Калесгос... Да чтоб тебя.','235400','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Давай, до встречи. Тебе наверняка будет интересно послушать их разговор. А у меня полно других дел, раз Калесгосу сейчас не нужна моя помощь.','','235401','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Здесь ощутимо пахнет магией... Хале где-то близко.','235507','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','В этой книжонке ни капли полезного... зачем было называть ее \"Тайны аберраций\", если тут одни общеизвестные факты? Бесит!','235509','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Синдрагоса? Не может быть. Это иллюзия?','235513','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, я всего лишь проекция.','235514','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я пришла попросить тебя вернуться на Драконьи острова.','235515','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я так и планирую. Но как видишь, у меня в Зимних Ключах куча неоконченных дел.','235516','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','...Хм. Подозреваю, без правого ботинка ничего не получится. Жаль.','235525','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой-ой!','','235527','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Досчитай до ста тысяч миллиардов, чтобы я успел поудобнее устроиться!','','235528','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Испытаем вон там, где побольше свободного места. Почему бы и нет? Пока результаты у нас неплохие.','235529','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Только не подсматривать, ладно?','','235530','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Что?! Пф-ф! Ну конечно!','235538','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хватит!','235539','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну почему опять!','235540','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Может, я найду в кои-то веки хоть один непроклятый артефакт!','235541','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я помню это место. Мирное с виду, но с невероятно мрачной историей.','235542','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','В Зимних Ключах есть одно очень теплое местечко.\n\nИди по тропе на запад и найдешь чудесные горячие источники.\n\nВстретимся там.','235545','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты в одиночку работаешь не меньше Аспекта. Твое упорство меня восхищает.','235547','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Дитя мое. Дыши глубже. Давай отвлечемся ненадолго. Я как раз знаю, где.','235548','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Бесполезно. Дикая магия просочилась в каждую снежинку. И это одни только Зимние Ключи.','235549','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Зачем мы здесь? Мне надо вернуться к работе!','235550','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','То есть ты за столько лет ни разу не побывала на горячих источниках? Везде говорится, что они очень знаменитые.','235551','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нежиться в горячей воде? У меня нет времени на баловство.','235552','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Совсем не баловство. А необходимость.','235553','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','$p! Давай, войди в воду. Покажи Хале, как это приятно.','235554','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну ладно. Но только на минуточку.','235556','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хм... Очень... неплохо.','235557','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хале, ты уже помогла Зимним Ключам, как смогла. Теперь твое искусство нужно нам.','235559','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вернись в Лазурные архивы хотя бы ненадолго.','235560','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Обещаю, что там ты скучать не будешь.','235561','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я долго просидела тут одна, это верно. Заманчиво. Сменить обстановку...','235562','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты помогла мне вспомнить, что мир не кончается Зимними Ключами.','235564','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты делаешь нужное дело. Я понимаю, как важно тебе снова ощутить себя тут как дома.','235565','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','...Спасибо. Я вернусь вместе с тобой, но имей в виду: я не знаю, останусь ли... и как надолго.','235566','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Фу! Гадость какая.','','235636','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не гарантирую точность этого заклинания. Телепортироваться под землю... трудновато.','','235638','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Удачи, $n.','','235639','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тира? Я Травар, капитан Стражей Тира. Мой долг – беречь его гробницу. А вы кто?','','235716','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пробудить... Тира? Но как... как сотворить такое чудо?','','235717','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я Коранос, дозорный титанов. А это $p. Мы намерены пробудить Тира.','','235718','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Жар и энергия пламени Тьмы насыщают материалы внутри. Чтобы пережить взаимодействие с такой печью, потребуются особые драгоценные камни.>','<Жар и энергия пламени Тьмы насыщают материалы внутри. Чтобы пережить взаимодействие с такой печью, потребуются особые драгоценные камни.>','235769','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Жар и шепот из Тьмы насыщают материалы в форме, создавая куски, пульсирующие пламенем Тьмы. Чтобы коснуться материалов внутри, потребуются средства защиты, изготовленные самим Фиракком.>','<Жар и шепот из Тьмы насыщают материалы в форме, создавая куски, пульсирующие пламенем Тьмы. Чтобы коснуться материалов внутри, потребуются средства защиты, изготовленные самим Фиракком.>','235772','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А... А-а-а...','','235778','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Апчхи!','','235779','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо, Абиссиан.','235789','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('С тобой все в порядке?','','235791','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Эмберталь вздыхает.','235793','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я хочу спасти драктиров... но теперь Саркарет под защитой Аберрия. Боюсь, мы его не остановим.','235794','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как знать. Я что-нибудь придумаю.','','235795','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что, будешь читать нотации?','','235798','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не угадал.','','235799','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы давно избегаем Аберрий, но время пришло. Помоги мне, Сабеллиан. Эмберталь надо пробиться через Серный гарнизон. Если будем работать вместе, то поможем ей.','','235800','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Аберрий... Считаешь, Эмберталь стоит крови моих драконов?','','235801','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я знаю, ты жаждешь узнать, что там спрятал Хранитель Земли. Туда все равно придется пробиваться с боем. А сейчас тебе помогут союзники.','','235802','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('...Хорошо. Мы придем.','','235803','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Абиссиан! Как я рад видеть тебя снова.','','235804','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Время пришло, Гневион. Ты готов к бою?','','235806','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты меня заинтриговал. Речь про Аберрий?','','235807','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Против нас – все силы Аберрия. Нам нужна твоя сила – и твой Черный Коготь.','','235808','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да, конечно... Мы отобьем Аберрий. Но я боюсь того, что увижу внутри.','','235809','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Без тебя нам не справиться, Гневион. Если хотим победить, надо объединить усилия.','','235810','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты прав, как обычно. Ничего нового. Не волнуйся, мы с Черным Когтем тебе поможем.','','235811','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хм...','','235812','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надеемся, что с твоей помощью. Но прежде нам надо в гробницу.','','235813','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Поврежденная матрица титанов отступает, словно испугавшись вашего появления.','','235814','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Принюхивается.> Кажется... начинается... А... а...','','235825','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой-ой... У меня же... аллер... а... а-а...','','235830','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Закали! Давите прихвостней Аспектов. Для сделки с Фиракком мне нужен каждый слиток, каждый кусок металла. Гоните прочь этих воров!','235832','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Закали! Неужто вы так слабы?! Мне что, послать вам на помощь жалких воинов стихий?','235833','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Раз это технология титанов, то я, вероятно, смогу ее починить. $p, ты пока проверь другую.','','235835','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Печать сломана! Скорее внутрь! Пока опять не закрылось!','','235836','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сабеллиан! И... Гневион?','235864','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Полагаю, Абиссиан и тебя уговорил ввязаться в геройства?','','235865','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гм.','','235866','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Раз мы все собрались, начнем.','','235868','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У меня нет слов, чтобы поблагодарить вас. Спасибо, что пришли на выручку.','235869','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Так мы сможем хоть что-то исправить и помочь вам.','','235870','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хм...','','235871','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что-то не так, Травар?','','235872','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне непросто здесь находиться. Тут погибли почти все Стражи Тира.','','235873','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Делай скорее то, за чем пришел. Моих друзей сожрали чудовища, которые прячутся в тенях.','','235874','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нам нужно проверить память. $p, скажи, когда начинать.','','235875','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Джарадины – заклятые враги черных драконов. Но медленно иссыхать в неволе – слишком жестокая участь даже для них.','','235879','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как давно они здесь?','','235880','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слишком долго.','','235881','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты пробивайся здесь, а я найду другой путь к Саркарету. Встретимся впереди и сразим его вместе.','235882','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо, $n. Я не забуду твоей доброты в час, когда мне требовалась помощь.','235883','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Сундук заперт на три замка, которые можно открыть только ключами из металла, закаленного в неугасающем огне. Груз также защищают обереги – если вы откроете сундук, об этом сразу узнает Фиракк.>','<Сундук заперт на три замка, которые можно открыть только ключами из металла, закаленного в неугасающем огне. Груз также защищают обереги – если вы откроете сундук, об этом сразу узнает Фиракк.>','235885','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я о нем позабочусь, а ты найди способ убрать щит!','235903','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Следуй за тьмой, если хочешь, но не тяни с собой наш народ. Пусть сами сделают выбор!','235907','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расколотое Пламя – мое! Я делаю с ним все, что хочу!','','235908','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы посмели воровать из моих горнил?! Я до вас доберусь, червяки. Вы сгорите в пламени!','','235928','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Черные драконы – истинные владельцы этого оружия!','','235936','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Марионетки титанов! Горите!','','235937','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Послушник! Сожги их своей ненавистью! Забери мои слитки из пепла их трупов!','','235941','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я верю в свой народ. Они объединятся ради доброго дела.','','235948','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чернокнижники глупы. Твои демоны тебя же и прикончат!','','236001','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Какая жалость. Воины бьют во все стороны, не думая, куда попадут.','','236002','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разбойники любят красться сквозь тень. И бить в спину своим же союзникам!','','236003','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Гневион понижает голос.>\n\nnТо бишь наш \"законный наследник\"?\n\nПохоже, его раны затягиваются очень быстро. Не удивлюсь, если он уже восстановил силы.\n\nК сожалению, он все еще считает, что трон черных драконов должен перейти к нему. Он живет прошлым. Как его воспитали, так он в прошлом и застрял.\n\nИ поэтому его нельзя допускать до престола. Наоборот, нам давно пора отказаться от дряхлых традиций. Я должен повести нас в будущее.','','236030','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Таинственное и зловещее укрытие, спрятанное от всего мира. И не зря. \n\nСами стены лаборатории пропитались эхом темной магии и запретных знаний. Оно вызывает и страх, и восхищение – и чтобы узнать, что же отец нам оставил, придется проникнуть в самые глубины.','','236032','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Никто не должен помешать нам войти в лабораторию. \n\nСейчас для нас нет ничего важнее.','','236041','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Серебряная чешуйка перенесла нас в воспоминание Тира. Оно мне незнакомо. Не узнаю этого места.','','236049','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я узнаю... это Тирисфаль. Неужели это легендарная битва с Закажем... Последняя битва Тира – его великая жертва.','','236050','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ничего. Одолеть меня не так-то просто.\n\nНо меня беспокоит то, что поступок Фиррака может значить для всех нас. В пламени Тьмы теряли свой путь даже те, кто был сильнее его. Таких примеров немало.','','236051','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('После этого Иронайа и Аркедас отправились на юг в Ульдаман. А Тир... так и не пришел.','','236053','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ульдаман... печальное, скорбное место. Аркедас с Иронайей ждали его очень долго... а мы потеряли там диск с памятью Тира.','','236054','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это же тварь, которая напала на мой орден! Значит... она охотилась на Тира?','','236057','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я этого не позволю. Врайкулы со мной. Они не такие, как твой народ... Кит\'икс бросил тебя.','','236058','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не такие, говоришь? Ха-ха-ха! Порядок не защищает от предательства. Может, врайкулы и с тобой...','','236060','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неужели Алекстраза тоже поддалась порче? Но как?','','236064','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<маниакально смеется>','','236065','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Довольно! Твоим играм пришел конец, Закаж.','','236066','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я умру... и тогда скверна поглотит Изумрудный Сон.','236067','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Магия... она не для смертных! Они слишком ничтожны!','','236068','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Древние боги... Все мы покоримся им...','','236069','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мой конец... неизбежен. Дорнозму... неизбежен.','','236070','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тир готовится к решающей атаке.','','236089','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неужели... Тир сильнее всего страшился, что Аспектов поглотит порча?','','236264','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне хочется взять и отвергнуть слова почитателя Древних богов...','','236266','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Думаю, все ответы мы получим, когда он проснется. Уходим.','','236267','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Хранитель Тир приходит в движение, но тут же отключается.>','','236271','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я пробыл на островах недолго, но уже многое узнал. И понял, что еще должен сделать.','','236483','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не забыл тебя, сестра. Никогда не забуду. Но ты не заслуживаешь провести вечность в одиночестве.','','236484','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вы чувствуете, что вас окутывает знакомое присутствие.','236485','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Кейлек... друг, скажи ему... нет, постой. Я должна сама ему сказать.','236486','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Самопожертвование Таресгосы коснулось нас обоих. Почту за честь, если ты поможешь проводить ее в последний путь.','','236487','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Таресгосе была уготована великая судьба, но в итоге она обошлась с сестрой жестоко. После Катаклизма нас предал один из своих. Переметнулся к Смертокрылу.','','236489','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сестра закрыла меня собой от атаки и получила смертельную рану. Чтобы продлить ее жизнь на Азероте, мы поселили ее душу в посох. Который теперь у тебя.','','236490','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я не сожалею ни о том времени, что мы провели вместе... ни о решении Кейлека. Я увидела полмира твоими глазами. Я почитаю за счастье, что наши дороги пересеклись.','236491','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Но пока ты меняешься под новый мир, я застыла во времени. Меня ждут другие пути.','236492','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Объединив магию синих и бронзовых драконов, мы перенесем душу Таресгосы из посоха. Подготовь могилу, а я начну ритуал.','','236493','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Таресгоса, дорогая моя сестра. Несмотря на то, что я твой учитель, я все равно постоянно чему-то учусь у тебя. Утрата ляжет тяжелым грузом мне на сердце. Если бы я мог вновь взлететь вместе с тобой в небо...','','236495','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но цепляться за прошлое и надеяться на милость судьбы – лишь пустая мечта. Теперь я это понял. Я ступлю в будущее один, но память о тебе будет греть мне душу.','','236496','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Кейлек, дорогой брат. Запомни только одно: я горжусь тобой.','236497','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прости... прости, что не защитил...','','236498','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это был мой выбор – и сейчас именно ты нужен миру, брат мой по стае. Я знаю, что ты поведешь ее к процветанию. Последняя глава моей жизни подошла к концу.','236499','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прощай, Таресгоса. Дорогая сестра, героиня, ученица и наставница. Мы не родные по крови, но были и останемся семьей.','','236503','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да взлетит твоя душа звездой к небосклону.','','236504','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я всегда ставлю интересы черных драконов на первое место.','','236684','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('$n! Благодарю за своевременное прибытие.','','236808','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В столь опасном месте надо глядеть в оба... очень важно сосредоточиться на деле.','','236846','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты так и бросишь друга в беде? А если я завтра вообще умру?','','236848','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Увидимся там, друзья.','','236866','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('На двести лет слизи хватит!','','236903','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Так ты решила поселиться здесь?','','236933','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Осквернитель!','','236979','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В каждом звере затаился монстр!','','236980','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Уступи своей ярости...','','236981','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хале, я советую особенно осторожно отнестись к этому...','236998','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хале, стой.','237009','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А... пчхи!','','237039','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А... а...','','237040','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кто-нибудь захватил табели учета? Пока будем тут все расчищать, мы столько часов себе набьем!','','237119','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что тебе удалось узнать, $n?','','237141','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да, $n?','','237144','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ой, прости, $n... Я отвлекся. Тебе что-то надо?','','237149','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не сегодня!','','237159','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Саркарет...','237168','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне жаль твоего друга.','','237169','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как ты и сказал, он выбрал свой путь. И все-таки... Саркарет заблуждался, но он заслужил достойного прощания по обычаям драктиров.','237170','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Конечно. Я пойду с тобой к месту его смерти.','','237171','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В Аберрии пока еще небезопасно. Нельзя отпускать их одних.','','237172','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Согласен. Ты с нами, герой? В такой путь лучше идти с компанией.','','237173','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надо поспешить. Тут полно народа – живого и не очень – который нам не обрадуется!','','237211','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы отыскали важное наследие Нелтариона. Пришла пора обратить его на пользу.','','237213','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо, что ты здесь. Используя найденный тобой камень, я могу изменить время и раскрыть тайны создания новой перчатки, которая поможет твоим соратникам-драктирам.\n\nПриступим?','','237219','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нелтарион был мне как брат. Мне стыдно, что он так много всего натворил втайне от меня.\n\nЗнай я, что он наделал, как он создал и подчинил вас... Я мог бы избавить вас всех от боли.\n\nНо, как бы мне ни хотелось, я уже ничего не могу сделать с грехами Нелтариона и своим невежеством.\n\nНо я могу помочь проложить новый путь для вашего народа, и на этом пути вам не нужно будет бояться, что кто-нибудь снова будет вами управлять.','','237223','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вернемся в Вальдраккен. Нам предстоит многое сделать.','','237226','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я изучил воспоминания, которые мы добыли... похоже, первый Хранитель клятв должен был даровать силу тем, кто служит Порядку.','','237228','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Саркарет... жаль, что ты так и не внял голосу разума.','','237231','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я вижу, как устроен Хранитель клятв. Зря Нелтарион решил поработить драктиров с его помощью.','','237232','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нелтарион сказал, что Эмберталь получит его сущность... ни один драктир из тех, что я видел, не смог бы принять такую мощь.','','237234','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вот он, $n...','237236','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чтобы выковать все части, нужно много времени. Желаю тебе удачи. Я узнаю, как наделить их силой, в которой нуждаются драктиры.','','237237','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты тоже обладаешь силой черных драконов. Я полагал, такое уже невозможно.','','237265','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что до Хранителя клятв... Думаю, у нас есть все, что нужно, чтобы выковать собственный артефакт.','','237266','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разногласия между черными драконами остры как никогда, но они не продлятся вечно.','','237299','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отнеси бомбы командиру армии. Он поймет, куда их применить.','','237328','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надеюсь, она понравится твоему колкому другу.','Надеюсь, она понравится твоему колкому другу.','237332','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты за заказом Агапанта? У меня все готово.','Ты за заказом Агапанта? У меня все готово.','237334','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне приснилась наша встреча... Тебе нужна набивная кукла?','Мне приснилась наша встреча... Тебе нужна набивная кукла?','237335','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Благовония! Высшего качества! Подходят для всех видов существ!','Благовония! Высшего качества! Подходят для всех видов существ!','237338','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отличный выбор! Приходи еще, если понравится!','Отличный выбор! Приходи еще, если понравится!','237339','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы должны собрать здесь темпоральные воспоминания и выяснить, что можно узнать.','','237357','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нам крупно повезло.','','237378','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я бы себе не простил, если бы кто-то пострадал из-за ненадежной телепортации.','','237379','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но если что... мы же ничего не расскажем Алекстразе?','','237380','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Аберрий много лет служил Нелтариону секретной лабораторией, еще со времен до нашего рождения. \n\nАберрий ищут многие и по разным причинам, но главное для нас – чтобы ничего оттуда не попало в руки наших врагов.','','237393','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Местные разведчики обратили внимание на возросшую активность джарадинов в этих местах.\n\nУчитывая твое открытие, предполагаю, что они планируют проникнуть в Аберрий и вызволить некое существо, которое они называют старейшиной.\n\nВозможно, это их бывший предводитель или какая-то другая, не менее важная личность?','','237398','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слава черным драконам.','','237402','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Последние четыре года я обучался у...','','237407','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это замечательно, Лазтак. Вот, держи.','','237408','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Агапант вручает Лазтаку стопку книг.','','237409','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Постой... Ты не хочешь узнать, какое образование я получил?','','237410','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты не побоялся стать воспитателем дракончиков. Уверен, у тебя серьезные намерения.','','237411','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А теперь за дело. У нас много работы!','','237413','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пора нам поговорить, $n.','','237419','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Еще! Мне нужно больше силы!','','237447','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я уже много лет не видела никого живого и материального, особенно свою стаю. Я... волнуюсь.','237449','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы рассчитываем на тебя.','','237500','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Опустить оружие! Это друг.','','237503','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Привет! Я так рада, что тебя послали мне на подмогу.','237570','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гордецы-паладины верят, что их молот несет справедливость. Но что если под ним окажутся твои друзья?','','237572','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Надо его остановить!','237573','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вот это я понимаю битва!','','237583','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Баллисты уничтожены!','','237681','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Солдаты, зовите сюда повелителей! Победа близка!','','237682','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты как будто уже меня знаешь, хотя мы видимся впервые.','237688','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Осторожно! Останки излучают странную магическую энергию...','','237690','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бывает, что могущественные синие драконы возрождаются в виде воспоминаний, когда их тела уже погребли.','','237691','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы со Стеллагосой можем их изучить. Ты же еще не всех синих драконов нашел?','','237692','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если повезет, моя многолетняя работа завершится, при условии... секунду. Ты же не призрак? Неожиданно увидеть кого-то из плоти и крови. Но приятно.','237693','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Драктир! Ты живешь, чтобы служить мне!','','237696','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','После пробуждения я долго изучала, что произошло с моей стаей за все это время.','237698','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Много дорог ведет в Зимние Ключи. Не раз их постигали перемены, но кое-что не менялось никогда. Ты.','237701','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Когда-то здесь был дом многих синих драконов, и мой тоже. Хочу верить, что когда-нибудь я закончу работу и он станет им вновь.','237703','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','...запись триста восемьдесят семь. Последняя. Дозор призраков Шандарала окончен. Мне тоже пора вернуться домой.','237709','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Банальное и некачественное проклятие. Не бойся, скоро растаешь.','237710','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Этим душам уже не помочь, но по крайней мере они освободились.','237718','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наверху Экспериментального крыла нас ждут драконозмеи.','','237719','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Они спустят нас на нижнюю платформу.','','237720','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хочешь, помогу?','237726','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет-нет, спасибо. У меня свои особые методы. Не стоит беспокоиться.','237727','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пора отвоевать свое.','','237728','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Готовьтесь. Долг нашего народа – остановить джарадинов.','','237729','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тут ничего страшного, Синдрагоса! В основном безвредные проклятия и сглазы. Уже выдохлись от времени.','237730','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','...Как скажешь. Тогда я просто посмотрю.','237731','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Может, я улечу на острова раньше, чем ожидала!','237732','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Вы смотрите на фигуру, и связанные с ней воспоминания проникают в ваш разум, будто они ваши собственные.\n\nВы восстали из мертвых. Вы видите некую фигуру. Это Король-лич. Он знает, что ваше имя – Синдрагоса. \n\nВы подчиняетесь его воле. Когда-то вы были яркой личностью, но теперь ее заменили горечь и ненависть.\n\nНенависть к Малигосу, который покинул вас. Горечь по отношению к миру, который предал вас.\n\nВо что вы превратились? Где заканчивается воля Короля-лича и начинается воля Синдрагосы?>','','237734','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что здесь произошло?','','237737','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не знаю, зачем я убеждаю саму себя. Я никогда не смогу очистить все до конца.','237738','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тише, малыш. Мы тебя не обидим.','','237739','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сколько, говоришь, дней прошло с последнего происшествия? Сорок?','237740','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Воспоминание не уходит; напротив, все новые и новые образы заполняют ваш разум.\n\nМерзкие отродья испытывают вашу силу, мучают вас. Готовят вас к сражению с врагами Короля-лича.\n\nВас окружают искатели приключений, вооруженные своей жалкой магией против вашей мощи.\n\nВ конце концов вас уничтожают... Но не полностью. Вашу сущность заключили в кристаллическую форму и передали некому Калесгосу.\n\nВы кричите от ярости. Они думают, что вы спокойны. Они не знают, что вы в ловушке.\n\nСнова никто не приходит спасти вас. Вы навеки заточены в кристалле.\n\nЕдва вы подумали, что воспоминания закончились, как они начинаются снова. Вы восстаете из мертвых, вы полны ненависти...>','','237741','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сорок один! Я не успокоюсь, пока не доведу до ста. Сорок – это даже не половина.','237742','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Этот еще несет знамя отца.','','237744','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','<Вы чувствуете, что воспоминания начинают просачиваться во все уголки вашего разума. Вам кажется, будто эти воспоминания – ваши.\n\nИзгнание из Аспектов. Великому Малигосу пришлось защищать Азерот от смертных в одиночку. \n\nДругие не понимают, что вы пережили. Вы сражались против Нелтариона в первых рядах.\n\nВы потеряли все. Синдрагосу, свою семью... даже свой разум. И все же вы пытались спасти Азерот от него самого.\n\nИ поэтому ваша некогда любимая сестра возглавила отряд искателей приключений, которые хотели убить вас.>','237745','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты здесь в полном одиночестве. Свет не слышит своих жрецов.','','237746','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Великолепные создания. Должно быть, тут их гнездовье.','','237747','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','<Воспоминание не исчезает; ваш разум наполняется новыми образами.\n\nБитва. Ваше уничтожение. Аспекты, опечаленные вашей смертью.\n\nИ все же кое-что уцелело. \n\nЭто \"кое-что\" называют Сердцем Магии. Никто не знал, что оно сохранило последние частицы вашей сущности. \n\nВы испытываете сожаление, глядя на лица тех, кого когда-то любили. Возможно, именно вы их предали.\n\nВам некогда размышлять об этом: воспоминания возвращаются к насилию. \n\nСмерть. Разрушение. Страдания. Это все, что вы теперь знаете, и спасения нет. \n\nАспекты и смертные заплатят за то, что сделали.>','237748','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Вы видите Нелтариона, бывшего Аспекта черных драконов. Воспоминания сменяют друг друга, словно ваши собственные.\n\nВы с подозрением относились к Нелтариону. Малигос доверял ему. Они вместе сражались с Галакрондом. Они вместе спасли Азерот.\n\nКакое вы имели право выступать против него? Вы отмахнулись от своих подозрений. Это просто паранойя – считать, что Аспект может поддаться порче.\n\nИ все же это случилось. Вы проклинали себя, когда начался бой. Вы должны были настоять на своем. Вы могли все это остановить.\n\nВсе это – результат вашего бездействия. Лазурной королеве Синдрагосе пришел конец.>','','237752','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Но сначала... еще пару минуток посижу. И правда – приятно.','237753','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Получается, они хранят верность Нелтариону. Откуда они? Из Аберрия?','','237754','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Видение упрямо не желает рассеиваться. Вместо этого в вашем сознании возникают новые образы.\n\nВы тяжело ранены. Прежде чем все померкло, вы успели увидеть, как синих драконов уничтожили в одно мгновение.\n\nВас бросили в холодном Нордсколе, вдали от вашего дома в Лазурных архивах. Вдали от Тайного склепа, где вы должны были обрести покой.\n\nВы могли бы добраться до Драконьего Погоста. Обрести покой там. Вы зовете Малигоса, своего возлюбленного, но он не отвечает.\n\nВы осознаете, что покоя не будет. Вы одни. Любовь к миру смертных сменяется ненавистью, а затем все исчезает.>','','237755','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это сделали оружием драконидов.','','237756','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись триста восемьдесят семь. Калесгос прислал за мной смертного. Честно говоря, я решила, что обо мне уже забыли. Очень... приятно, когда тебя помнят.','237766','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','По-моему, у меня кончились идеи. Если перековка кристалла не поможет, я не знаю, что... О! С возвращением. Нашлось что-нибудь?','237767','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','И тебе добро пожаловать. Я Киригоса.','237785','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Судя по этим фрагментам, мы все еще наблюдаем последствия погребения Синдрагосы и Малигоса.','','237786','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сенегос упоминал, что это случается нечасто, но как оказалось, мы плохо представляли себе истинные масштабы.','','237787','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это мой друг $p.','','237788','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','А! Кажется, я тебя знаю – слухи о твоих путешествиях дошли даже до нашей деревушки. Твой визит для нас честь.','237790','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Но как я понимаю, ты по важному делу?','237791','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В своем роде. Ты слышала зов с островов? Синие драконы возвращаются.','','237792','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да... Слышала.','237793','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ах... $p, не хочешь прогуляться по деревне, пока мы болтаем? Здесь столько интересного.','','237795','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да. У меня обычная, рутинная жизнь. Я знаю всех местных жителей. У меня даже свой дом.','237805','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Дом?! О... Ясно...','','237808','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сенегос, наверное, уже в Тайном склепе. Мы с Алекстразой скоро тоже придем.','','237810','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не хочу давить. Может, дать тебе немного времени все обдумать?','','237811','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо за понимание. Если ищешь, где отдохнуть, рекомендую таверну \"Пьяный хозен\".','237812','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И Малигос, и Синдрагоса погибли мучительной смертью. Похороним их останки как подобает, чтобы хоть как-то их почтить.','','237814','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','А, вот ты где.','237819','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мне тяжело отказывать, но я не могу вернуться на острова.','237820','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не сейчас... Еще не скоро.','237821','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пойми – в этой стране, среди ее жителей... я впервые в жизни чувствую, что сама контролирую свою жизнь.','237824','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты бы подружилась с клыкаррами – это такой народ. Они недавно преподали мне важный урок.','','237825','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В чужих краях или в родных...','','237826','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('...семья остается семьей.','','237827','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Спасибо.','237829','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отлично! Подведи шар ближе к верхнему входу в гробницу и запусти его в небо.','','237830','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Запереть клетки. Скоро выдвигаемся в Аберрий.','','237893','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И снова скользящие дракозмеи страдают из-за черных драконов. Значит, мы должны вмешаться и все исправить.','','237897','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не сержусь, $n. Только печалюсь.','','237898','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тяжко видеть, как ссорятся Гневион и Сабеллиан. Даже на минуту не могут забыть о своей гордыне.','','237899','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сегодня мы напомним врагу, что эти земли – под нашей защитой.','','237912','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Командир, разведчики обнаружили драконьи гнезда на востоке.','','237917','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не к добру это.','','237918','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я тоже. За полем боя присмотрит командир, а мы отправимся на разведку.','','237919','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Начнем, $n?','','237929','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А, вот ты где. Мы не хотели начинать без тебя, $n.','','237932','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Встретимся у входа в Серный гарнизон, Сабеллиан.','','237935','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В этой области – сейсмическая активность. Собирать кристаллы будет удобно. Лови их, пока не разбились!','','237943','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что? Я же не виноват, что у тебя нет каски!','','237945','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Все забыли про пещеру Заралек. И про наш гарнизон, который до сих пор сражается за черных драконов. \n\nПора отправить врагам весточку.','','237958','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прикройте фланги! Не хочу никаких сюрпризов от Расколотого Пламени.','','237959','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сабеллиан, здесь что-то темное вытягивает из меня силу. Ты это чувствуешь?','','237960','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чувствую. Держаться непросто. К счастью, на наших друзей это не действует.','','237961','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надо во что бы то ни стало остановить Саркарета. Нельзя, чтобы он заполучил силу этого места.','','237962','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты в ловушке, Саркарет! Остановись, пока все твои воины не умерли.','','237963','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы лучше умрем, чем наденем ярмо.','','237964','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тайны этого горнила – наши! Когда мы обретем их, то больше никому не станем служить!','','237965','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наш строй сыпется, Саркарет! Нам их не сдержать!','','237966','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разрешите предложить иную тактику.','','237967','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я помогал Нелтариону создавать... могучих существ.','','237968','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Одно из них может тебе помочь... хотя оно, скорее всего, убьет всех. И врагов, и наших.','','237969','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Выпускай его!','','237970','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но там еще остались наши!','','237971','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Их имена не будут забыты.','','237972','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что это такое?','','237973','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы пытались улучшить доспехи Нелтариона. Сделали много попыток.','','237974','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты видишь одну из самых удачных. Неидеально, конечно, но незваных гостей уничтожит.','','237975','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наконец-то. Хватка ослабла.','','237979','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неужто Саркарет овладел силой, которая может разъедать разум.','','237980','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У предателя нет такой силы, сын мой. Это пробуждается зло... оно чувствует вас.','','237981','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отец?','','237982','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я лишь отголосок. Я не могу остановить захватчиков и уберечь свои секреты.','','237983','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но вы, мои дети, можете.','','237984','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('$p, спасибо за столь быстрое возвращение.','','237989','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я рассказал всем собравшимся о своих находках, но мне нужна твоя помощь.','','237990','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Думаю, вы слышали немало историй об этом месте и о том, как оно меня погубило.','','238003','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не стоит им слишком доверять.','','238004','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эти залы – ваше наследие. Чужаки не имеют на них никакого права.','','238005','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Оцепить здесь все!','','238006','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гневион, я возьму группу воинов и отправлюсь за Саркаретом. А ты ступай другой дорогой. Проверь, что здесь интересного.','','238008','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Выкладывай, Зерос. Опять во что-то вляпался?','','238009','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мудрый план. Я помогу обоим, чем смогу.','','238010','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это же Барон!','','238011','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А ты синий дракон. Хоть и молодой.','','238013','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ха-а... Похоже, джарадины тренировались на этой стене. Хватай их вещи и беги!','','238014','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тсс! Я выбрал для дела облик повзрослее. Ты меня так запалишь!','','238015','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А... да... ну-у... тут такая ситуация...','','238016','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тот гоблин считает тебя за прислугу. Почему ты просто его не съел?','','238018','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Думаешь, охотники знают все о диких тварях? Посмотрим, насколько послушны твои звери!','','238021','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И потом... Я не такой жестокий. Я беру плату золотом, а не кровью.','','238022','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Превосходно! Ни одного джарадина не встретилось, да?','','238024','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В этих залах я создавал чудеса, которые могут изменить мир.','','238026','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Черные драконы попросили нас достать кое-какие артефакты. Бери снаряжение – и давай на ту скалу!','','238027','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отлично! Может, они разрешат нам, м-м, взять парочку? Чтобы изучить... Всего парочку. Всего. Парочку...','','238028','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тысячелетия спустя они... слегка одичали. Подчинить их сможет только сильный духом.','','238029','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я... вообще-то тоже результат эксперимента. Твое прошлое воплощение хотело меня уничтожить.','','238032','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты – кровь моей крови. Ты сумел выжить. Ты силен. Как я.','','238034','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если это правда, Ноздорму... Тир может помочь в бою с драконами Бесконечности.','238047','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У меня снова просьба, $p. Коранос, не присоединишься? Твои познания в технологиях титанов тоже полезны.','238048','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я доставлю вас на место побыстрее.','','238050','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Удачи, $p!','','238051','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вижу, мы найдем общий язык...','','238053','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Дрогбарские улитки производят дивные ароматы, когда сыты. Иди покорми их и принеси янтарный жир. Все будут в выигрыше!','','238070','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пламя и тьма – две великие силы.','','238072','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если их объединить, они станут еще могущественнее. Научись соединять одну с другой, и тебя ничто не остановит.','','238073','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Видишь теперь, какая сила таится в пламени Тьмы?','','238075','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да-да, весьма грозная. Но управлять ей, пожалуй, слишком опасно.','','238077','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тебе хватит силы духа, сын мой. Представь, чего ты сможешь добиться.','','238078','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нам не справиться!','Нам не справиться!','238085','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты дал нам надежду, Ноздорму. Но пора действовать.','238086','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я так надеялся на драктиров. Увы, они меня только разочаровали.','','238089','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но ты справишься, Гневион. Ты продолжишь дело моей жизни.','','238091','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разумеется, отец.','','238093','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Великолепно. Поздравляю.','','238100','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне выпала честь наблюдать за созданием драктиров.','','238101','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('К несчастью, они оказались нестабильны. Но там, где все видят тупик, я вижу новый путь.','','238102','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Из вас выйдут неплохие подопытные крысы.','','238104','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Порча Смертокрыла коснулась меня еще до того, как я появился на свет. Но я и не представлял, как далеко он может зайти.','','238110','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отец... двигался к цели упорно и непреклонно. Он никогда не одобрял полумеры.','','238111','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И тебе это нравилось?','','238112','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Воздух пропитан извращенной злобой.','','238114','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Все закончится сегодня. Это последняя чистка.','','238115','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Когда ты вылупился, так и было? Остальные были совсем отравлены?','','238116','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что это такое?','','238119','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разумеется. Я сделаю все, чтобы вернуть Тира.','','238124','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мягкие! Слабые! Горючие! Я спалю вас дотла, червяки!','','238127','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы надеемся, что сможет. $p, пора снова пустить ее в дело.','','238130','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Монахи делят свою сущность на три части. Поглядим, что я сделаю с твоей душой.','','238133','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Аспекты... дают мне надежду...','','238134','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты справляешься даже лучше, чем я ожидал.','','238138','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы – драконы. Воспарим!','238141','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Погоди. Ты знал, что на нас спустят этих тварей?','','238142','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ну конечно! А еще я знал, что ты преодолеешь испытание.','','238144','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как-то это... странно. А можно кое-что поменять в \"деле твоей жизни\"?','','238145','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Этого я и боялся. Раз ты был создан, значит, твой дух все-таки слаб. Мой истинный сын наверняка не таков.','','238146','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Жалкие рыцари смерти. Вокруг них все гниет.','','238150','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сабеллиан, кровь моей крови, ты – мой наследник. Но знай: много кто пожелает украсть твое наследие.','','238152','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Джарадины прямо сейчас готовятся пойти в атаку. Их надо остановить.','','238153','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не отступлю ни на шаг.','','238155','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Джарадины лезут на стены! Держать строй, герои!','','238156','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Враг разбит! Мы победили!','','238158','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Достойная победа, сын мой. Меньшего я и не ожидал.','','238160','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но горнило до сих пор в опасности. Осталось лишь одно, последняя угроза.','','238161','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Только скажи – и я устраню ее.','','238162','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Свобода!','','238163','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как этот джарадин проскочил мимо нас?','','238164','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Старейшины не было среди атакующих. Они пытались освободить его.','','238165','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тысячелетиями он весьма успешно питал мое горнило.','','238166','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты вытягивал силу из джарадина для экспериментов?','','238167','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Какая жалость. Придется от него избавиться, а не то проводники взорвутся и убьют нас всех.','','238168','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отлично. Проводники целы.','','238179','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но придется их зарядить. Думаю, тут сгодятся свежие тела джарадинов снаружи.','','238181','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты что, хочешь, чтобы я... Нет.','','238183','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да, я ожидал от тебя большего. Неважно. Возможно, Гневион будет решительней.','','238184','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Горнило ждет! Пора остановить Саркарета.','','238185','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неужто вы думаете, что сможете отобрать у меня горнило?','','238193','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я скорее тут все уничтожу, чем отдам!','','238195','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Он хочет залить комнату магмой! Гневион, мы должны ее сдержать!','','238196','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Герои, остановите его! Мы выиграем для вас время!','','238197','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне жаль вас. Шатаетесь по священному место, но не видите его сути.','','238207','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Никто из вас не покинет Аберрий живым.','','238208','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Господин!','238214','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Докладывай.','','238215','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Лоялисты засели в экспериментальном крыле, а в лабораториях кишат искаженные твари.','238216','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А другое крыло?','','238217','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы сдерживаем джарадинов, но они не сдаются.','238218','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо, Кавия.','','238219','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хочешь покормить улиток?\n\nХым-м-м... Ладно! Думаю, за мной должок.','','238223','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Теперь, когда с этим отбросом покончено, я вам помогу.','','238224','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Уберу лаву и открою путь к Саркарету.','','238225','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гневион, мне совсем не нравится эта ситуация.','','238226','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И мне. Но Саркарета надо остановить. Будь начеку.','','238227','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Саркарет отправился в сердце горнила другим путем. Тем, что сейчас перекрыт.','','238242','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Остается лишь одна дорога. Ее охраняет зверь, который умеет только жрать.','','238254','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Впечатляет. Но не время почивать на лаврах. Саркарет внизу.','','238259','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У шаманов – полезные тотемы. Им не хватает только... капли темной силы.','','238262','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Друзья! Какие новости из Аберрия?','238278','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы истребили мерзких отродий Нелтариона. Увы, порча уже успела поработить Саркарета, и его пришлось убить.','','238281','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нам всем придется выучить этот урок.','','238283','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Так и есть, брат. Встретив темное прошлое нашего рода лицом к лицу, мы нашли его будущее.','','238285','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы решили, что нас должен возглавить Абиссиан.','','238287','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Его мудрость и сострадание укажут нам новый путь.','','238289','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Согласна. Принимаешь ли ты этот пост, Абиссиан?','238293','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Почту за честь. Но мне нужен мой верный генерал и дипломат.','','238296','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вот и окончилось ваше путешествие, герои.','','238323','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('О чем ты? Где Саркарет?','','238324','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты вел нас не к Саркарету.','','238325','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты всегда был умен, Гневион.','','238326','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не могу... пошевелиться...','','238327','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что... ты такое?','','238328','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я – шепот во тьме.','','238329','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да будет так. Займи свое место в нашем совете... Хранитель Земли.','238330','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я надеялся, что вы оцените мои дары. Но вы их отвергли.','','238331','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Жители Вальдраккена! Отныне Абиссиан будет служить своему народу как Хранитель Земли и Аспект черных драконов!','238334','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А вот драктиры – приняли. Они были созданы, чтобы служить.','','238336','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы перед тобой не склонимся, лжец!','','238342','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Берегись, маг. Ты играешь с опасными силами. Со взрывоопасными.','','238359','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отчего тут так мерзко?','','238361','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кажется, будто оно где-то за гранью. Источает оттуда чистую темную энергию.','','238362','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Саркарет сломался из-за жажды власти. Что теперь будет с моими солдатами?','','238366','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неужто это судьба всякого, кто примет его наследие?','','238367','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расколотое Пламя не будет служить!','','238438','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Его сила – моя!','','238439','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мое правление начнется, как только вы умрете.','','238440','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Склонись и пади!','','238441','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я тут повелитель.','','238442','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вот и конец твоей судьбе.','','238443','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ах-х... ничего не вышло. Ну надо же.','','238444','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расколотое Пламя победило.','','238445','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Драконам конец.','','238446','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тут... все... мое...','','238447','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы слабеете!','','238451','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Моя сила растет!','','238452','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Испепелю!','','238453','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вечные страдания.','','238454','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бездна манит.','','238455','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бездна повинуется мне.','','238456','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Узрите мое вознесение!','','238468','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Умирающая звезда вас спалит!','','238469','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я выброшу вас в пустоту!','','238470','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тут энергия такой силы! Она покорится лишь могучим!','','238471','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тьма покорилась мне!','','238472','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Знаете... мне ни капли не хочется сюда возвращаться.','','238473','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да тут про все можно так сказать.','','238474','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ну что ж. Тогда не будем тянуть.','','238475','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я поставлю мир на колени!','','238476','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отец... дай мне силу!','','238477','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Звезды раздавят вас!','','238492','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Во тьме сокрыты дивные знания!','','238493','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Узри бесконечную тьму! Надежды нет.','','238494','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ваши тела пропадут в вечной пустоте!','','238495','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Для вселенной вы – ничто.','','238496','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Оно было глубже океанов Азерота, запутаннее песков времени, темнее бездны...','238499','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я немного отвлеклась лишь в Тернистой долине. Но здесь во мне вновь загорелся потухший огонь надежды.','238500','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если горе доберется до Цветущей Зари, я впущу его в свою жизнь.','238501','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Однажды мы еще полетаем над Азеротом.','238502','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твоя требуха украсит мои доспехи!','238513','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Друидам не хватает воображения. Зачем быть чем-то одним, когда можно – всем сразу?','','238514','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Какая сладкая боль.','238515','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твои крики ласкают мой слух.','238516','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Твое тело... беззащитно.','238517','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Меня что... победили?','238518','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мои прекрасные доспехи!','238519','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я сорву мясо с твоих костей!','238520','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Моя плоть страдает, но вам будет больнее!','238523','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Страдайте!','238528','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Очищающий огонь!','238532','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вам конец.','238533','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Поддайтесь ужасу!','238534','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Боль и тоска.','238537','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В каком-то роде. Но видя, к чему это приводит, я начинаю сомневаться.','','238550','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хуже. Они не просто жалкие останки его наследия. Они верно служили и шли за ним в безрадостное будущее.','','238551','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Непомерная цена, но безраздельная власть другой ценой не дается.','238552','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как я и надеялась. Итак?','238553','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мятежники запасают под городом взрывчатку, чтобы взорвать акведук!','','238555','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Охотники на демонов танцуют с клинками... вслепую? Какое безрассудство.','','238557','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Закали! Выбейте дверь! Пробейтесь к старейшине!','','238570','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сгодится на топливо.','','238571','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слишком просто.','','238576','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я тоже пойду. Помощь лишней не будет.','238597','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Встретимся в Пещерах змеев, $n.','','238601','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Враги Закали падут.','','238602','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Старейшину... надо... спасти...','','238603','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы убивали драконов. На что вы надеялись?','','238604','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наверх!','','238605','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ломайте их ряды!','','238606','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Закали, на стену!','','238608','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Падаем!','Падаем!','238618','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Земля, защити!','Земля, защити!','238619','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ко мне, Игнара.','','238620','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пируй, Игнара.','','238621','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('За старейшину!','За старейшину!','238622','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ого, ты уже здесь, $n! Спасибо, что так быстро! Пора покончить с этой угрозой.','','238623','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сомкнуть щиты!','Сомкнуть щиты!','238626','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Давите их!','Давите их!','238629','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Волна магмы!','Волна магмы!','238630','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слабые сгорят.','Слабые сгорят.','238631','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эти жалкие существа... неужели отец так не дорожил ничьей жизнью?','','238632','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Он желал лишь одного: создать будущее, где черные драконы поработят всех остальных.','','238634','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как... как прежде и я.','','238635','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пламя поглотит всех!','','238638','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты не похож на него, Сабеллиан. Ты хочешь, чтобы мы процветали, а не порабощали. Это достойная цель.','','238639','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо, Гневион. Но мне есть куда расти.','','238641','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наш огонь сжигает!','','238642','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мерзкие создания. Противоестественные твари!','','238643','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Они не сами решили появиться на свет. Прямо как я.','','238644','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гневион, я не то...','','238645','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я тоже вызываю у тебя отвращение?','','238646','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы познакомились совсем недавно, но я вижу, что как дракон ты вылитый я. Если я когда-то принижал тебя, мне искренне жаль.','','238647','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Отнесите чешуйку к гробнице Тира. Соберите там воспоминания. Мы обязаны вернуть Тира.','238688','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Взаимно, друг мой.\n\nСеребряная Длань еще при тебе? Держи ее поближе. Возможно, она еще обретет силу вновь.\n\nНам многое нужно обсудить, но сперва мы должны исполнить свой долг. Присутствие в гробнице стольких творений Тира не останется незамеченным.','','238689','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я последний из Стражей Тира. Мои соратники пали в бою с охотником Мордотом, пока мы искали Серебряную Длань Тира. Они защищали меня...\n\nКогда я узнал, что гробница обрушилась не полностью, я встал на ее страже. Это меньшее, что я мог сделать. \n\nОрден Серебряной Длани жив до сих пор, но когда не станет меня, Стражи Тира навсегда исчезнут.','','238690','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Проверим-ка ваш болевой порог.','','238701','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Еще один неудачный эксперимент.','','238702','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Потом тебя выброшу.','','238703','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я ничего и не ждал от таких слабых подопытных крыс.','','238704','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Радуйтесь... потом... вы будете страдать...','','238705','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Умеете ли вы адаптироваться к обстановке?','','238707','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Големы, тут нужна ваша помощь.','','238711','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как вы переносите экстремальные температуры?','','238717','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Думаю, результат будет смертельным.','','238740','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слуги Аспектов сгорят!','','238741','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Растопка для пламени.','','238742','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Снова в прах.','','238743','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И это хваленая сила драконов? <Смеется>','','238744','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Он больше... не отберет... мою кровь...','','238745','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расплавься!','','238746','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Поджаривай плоть.','','238747','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Огонь покоряет все.','','238748','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вулкан жаждет жертв!','','238749','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кости чернеют.','','238751','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Конец тысячелетиям мучений. Грядет аннигиляция!','','238754','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ничто не потушит моей ярости!','','238755','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Меня не погасить!','','238756','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пепел ваших трупов затянет небо!','','238760','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Лагерь хорошо охраняют. Придется прорываться с боем.','','238764','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Возможно, хотя бы части кровопролития получится избежать.','238765','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Брать пленных сложнее, но я знаю заклинание, которое сделает это возможным.','238766','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','И у меня есть пара идей: пока мы пытаемся предотвратить взрыв, попробуем заодно узнать, что движет мятежниками.','238767','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Акведук в безопасности, и все благодаря тебе, $n. А наших пленников отправят в бастион Стальных холмов.','238794','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('На базе чешуеклятов они ответят за все! Возможно, лучшие воины Вальдраккена даже дадут им немного свободы.','','238795','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тайный склеп – место, где мы хоронили своих мертвых. Мы, драконы, живем довольно долго, но старость в конце концов забирает всех нас. Исключение – Аспекты, они играют по другим правилам.\n\nНадо полагать, Калесгос сообщил тебе, что собирается упокоить сущности Синдрагосы и Малигоса.\n\nНо мы не можем этого сделать, пока в Тайном склепе такой беспорядок, не так ли?','','238799','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Самый первый драктир вышел слишком яростным и неуправляемым.','','238805','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но зато можно изучить хаос, который он творит.','','238806','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Первому прототипу недоставало силы. Следующего мы наделили силой тайной магии.','','238807','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('К несчастью, существо с нестабильной магией бесполезно. Так что мы улучшили технику слияния.','','238808','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я оставлю от вас мокрое место!','238809','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Глотайте свои зубы.','238810','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','На куски порвала.','238811','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я – вершина природы.','238812','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сюда, воины!','','238813','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Закали, ко мне!','','238814','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Раздавлю!','238816','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Переломаю!','238817','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Кости треснут!','238818','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сокрушающий рык!','238819','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Распотрошу.','238820','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эта консоль во всем похожа на те, которые встречаются в Тирхолде. \n\nСудя по всему, ее установили позднее, чем консоль на верхнем ярусе гробницы, или же ее недавно починили.','','238826','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Осколки костей.','238830','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Давить игрушки!','','238831','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Раздавил!','','238832','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мокрое место.','','238833','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бойня!','','238836','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Всех разбросаю!','','238837','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Разорву пополам!','','238838','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Взрыв!','','238843','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Взорвитесь изнутри!','','238844','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сокрушу!','','238846','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Умрите, мелочь.','','238847','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Низшая тварь.','','238848','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Знай свое место.','','238849','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я само совершенство.','','238850','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я... не могу... проиграть...','','238851','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Обращу в ничто.','','238861','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сотру вас.','','238862','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прощайтесь с жизнью!','','238865','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Воплощение совершенства.','','238867','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Узрите мою силу!','','238868','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Смертные букашки. Топливо для адского пламени.','238877','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Голод не стихает.','238880','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Прямо в глотку.','238881','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Главный кузнец! Убей этих мелких тварей и забери то, что они похитили!','238883','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет! Главный кузнец! Я отомщу за тебя.','238884','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мироздание сгорит в огне!','238886','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Водопад лавы.','238887','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Стена пламени!','238888','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Снова лишь тень.','','238890','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Последняя вспышка перед смертью.','','238892','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вам не сбежать от тьмы.','','238894','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мрак подступает.','','238897','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Консоль наполняется энергией. Она ждет вашей команды.','','238899','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пробудитесь, тьма и пламя!','','238901','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нас не остановить.','','238902','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нас заметили! Врассыпную!','Нас заметили! Врассыпную!','238903','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Жалкая попытка.','','238905','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нашу мощь не превзойти.','','238907','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хр-р-р... Мм-м? О... Теперь я могу вернуться в другую постель. Спасибо.','Хр-р-р... Мм-м? О... Теперь я могу вернуться в другую постель. Спасибо.','238909','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Жуса громко шмыгает носом.','','238910','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я... вечен...','','238913','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пылающая тьма пожирает всех.','','238915','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Взрыв теней!','','238917','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Обжигающий сумрак!','','238919','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Иссушающая тень.','','238932','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Теперь вы бесполезны.','','238933','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Конец вашим жалким жизням.','','238934','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бесплодная попытка.','','238935','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Шепот... никогда... не умолкнет...','','238936','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вы и правда ожидали чего-то иного? Какая нелепость.','','238937','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Филигоса \nМертва – неизвестная болезнь\n\nРеликсигос\nМертв – Пылающий Легион\n\nСенегос\n|cFFcc1a1aМертв – старость, в кругу друзей и родных|r\n\nВенигоса\n|cFFcc1a1aМертва – неизвестная болезнь|r\n\nКуригоса\nМертва – предательство Нелтариона\n\nДулигоса\nМертва – предательство Нелтариона\n\nЗалкигос\nМертв – предательство Нелтариона\n\nЭмигос\nМертв – предательство Нелтариона\n\nСтелагос\n|cFFcc1a1aМертв – убит помраченными|r','Филигоса \nМертва – неизвестная болезнь\n\nРеликсигос\nМертв – Пылающий Легион\n\nСенегос\n|cFFcc1a1aМертв – старость, в кругу друзей и родных|r\n\nВенигоса\n|cFFcc1a1aМертва – неизвестная болезнь|r\n\nКуригоса\nМертва – предательство Нелтариона\n\nДулигоса\nМертва – предательство Нелтариона\n\nЗалкигос\nМертв – предательство Нелтариона\n\nЭмигос\nМертв – предательство Нелтариона\n\nСтелагос\n|cFFcc1a1aМертв – убит помраченными|r','238938','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Восстаньте, горы!','','238943','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Земля поднимается!','','238944','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Земля повинуется мне.','','238945','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вас завалит обломками.','','238946','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Крушение близко.','','238947','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сгиньте во мраке.','','238951','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тьма пожрет вас.','','238952','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Покоритесь тьме!','','238953','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Пируйте, дети мои!','','238954','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бездна голодна.','','238955','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сдайтесь – и получите невообразимую мощь!','','238957','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наглые смертные! Если жаждете смерти, вы ее получите.','','238958','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я устал от ваших жалких потуг. Забвение ждет вас!','','238959','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Моя воля – это конец всему!','','238961','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Конец неизбежен.','','238962','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ваши останки поглотит бескрайний мрак.','','238963','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты считать-то умеешь? Или хочешь меня надуть?\n\nЯ сказал \"три шкуры\" – не меньше!','','238968','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Бликрез криво улыбается.>\n\nВдвое больше или гуляй.','','238976','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Огонь с небес!','','238980','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Смотри, Вакан. Эти букашки тебя даже не заметили.','239005','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Они просто нашли добычу полегче, Голна.','','239006','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не волнуйся. Я тебя спасу и раздавлю эту мелочь.','','239007','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Единственная мелочь тут – это ты.','239008','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я... гасну.','239049','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да уж, твоя ярость сравнится разве что с огнем свечи.','','239050','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Моя сила... угасает.','','239071','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Такому слабаку, как ты, даже пламя Тьмы не поможет.','239075','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вижу... Темные Земли... и... вечную битву...','','239083','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я... успела... первой...','239084','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Все верно. Мы относились к драконидам так, будто они хуже нас. Наше отношение к ним не должно зависеть от нашей силы или от их положения. Оно должны быть основано на том, кем мы решили быть. Благодарю тебя, $n. Теперь дальнейший путь ясен.','239115','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я тоже устала от этого восстания, $n. Но хороший исход невозможен, если мы и дальше будем закрывать глаза на то, что их беспокоит. Мы пытались одолеть мятежников, и это лишь обострило ситуацию. Не стоит продолжать делать то, что ни к чему нас не привело.','239117','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Лава, восстань!','239120','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Знатное извержение.','239121','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Безудержное пламя.','239124','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я вижу, что ты, $n, относишься к претензиям мятежником скептически – как и я. Но ты говоришь, что мир возможен, если мы выслушаем их. Если есть хоть какой-то шанс заключить мир и обойтись без дальнейших жертв... мы не можем его упустить.','239125','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Почувствуй жар!','239126','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я станцую на вашем пепле!','239128','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Палящая тьма.','','239129','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Обжигающие тени.','','239131','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ох, ты же знаешь, какими несносными бывают эти глубинные живодеры. Вскоре они доберутся до Лоамма, если только не выгнать их из пещеры.','239139','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это тебе. Спасибо, что не пустил живодеров в Лоамм. А то бы провоняли весь город. Ну, и сожрали бы всех.','239146','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Покоритесь гибели.','','239153','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Тьма разрастается.','','239154','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вот и пришли ваши сумерки!','','239157','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Надеюсь, вылазка получилась полезной, моя королева.','239164','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Именно так. Она не была бы такой, если бы не $n. Теперь я лучше понимаю, как рассуждают мятежники.','239165','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это поможет нам победить их?','239166','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет. Но эти мятежники – наши сородичи. Я надеюсь, что нам не понадобится \"побеждать\" их.','239170','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Белафон никуда отсюда не денется, $n. Надеюсь, скоро это восстание наконец окажется в прошлом.','239201','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да? Чего тебе надо? Не видишь, я занят.','','239203','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я сейчас очень занят. Приставай к кому-нибудь еще.','','239205','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Опять ты?\n\nПогоди...\nА мы вообще встречались?','','239210','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('О-о-о!','О-о-о!','239213','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А-а-а!','А-а-а!','239214','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('О нет!','О нет!','239215','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не могу смотреть!','Не могу смотреть!','239216','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ого!','Ого!','239218','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Хотел бы я так же...','Хотела бы я так же...','239219','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Снова ты? Чего ты хочешь?\n\nМне больше нечего тебе сказать. Брысь отсюда! Пожалуйста. Мне надо закончить отчет.','','239264','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Даже я, само сердце Аспектов, предам тебя, Тир.','239341','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прибывшие сюда черные драконы так и поглядывают на эту ракушку! Меня это нервирует. Забирай, пусть дите радуется.','','239347','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Взрывчатка! В моем доме? Не дай им сделать что-нибудь непоправимое.','239355','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Почему оно так кричит?!','','239356','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ай! Оно идет на меня!','','239357','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Уходи, плосклапое чудище!','','239358','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я боюсь, я боюсь, я боюсь...','','239359','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Если можешь взять пленных – пожалуйста, но не подвергай опасности акведук. Взрывы надо предотвратить!','','239360','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это были мои родные и друзья. Многие погибли из-за предательства Нелтариона. Некоторых, увы, мы потеряли позже.\n\nБыло время, когда я вспоминал их лица каждый день. Должен признать, что на старости лет я уже позабыл, как многие из них выглядели...\n\nВпрочем, не будем о грустном. Однажды я увижу их снова.','','239372','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Конечно. Малигос, о котором тебе наверняка доводилось слышать, был Аспектом синих драконов.\n\nЕго разум помутился десять тысяч лет назад, когда Нелтарион перебил почти весь наш род.\n\nНо не так давно он пришел в себя... и тут же попытался уничтожить всех магов в Азероте, считая, что они недостойны чародейской силы. Он не желал изменить свое мнение и был убит. Позднее юный Калесгос занял место Малигоса среди Аспектов.\n\nНо до своего безумия, до предательства Нелтариона Малигос был... радушным. Добрым. Они с Синдрагосой часто устраивали в Лазурном Просторе большие сборища! \n\nЯ скучаю по тем беззаботным дням, когда смех эхом отдавался в Лазурных архивах.','','239374','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что тебе нужно? До всего этого у меня была куча дел в Нексусе.','','239375','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Все тебе расскажи, а? Хм...\n\nРаньше это были владения Малигоса. Это место пронизано магией, которую он открыл, – в основном, когда был безумен.\n\nТеперь я здесь всем распоряжаюсь. И при мне никакие смертные не будут расхаживать тут и хватать все подряд – это попросту опасно.\n\nБольше я ничего не скажу. Мои исследования и находки касаются только меня.','','239381','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мальдра расшифровала код мятежников из найденных тобой листовок, $n. Пора покончить с провокациями Белафон!','239466','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Старейшины джарадинов – жестокие воители. Убить их будет непросто, но мы должны справиться.','','239478','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Старейшины пали! Бесчисленные драконьи жизни спасены. И отомщены.','','239479','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расколотое Пламя? Опять? Они что, не знают, что мы тут важным делом заняты? Прогони дозорных, чтобы мы продолжили работать.','','239480','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эй, Рэннан! Там, на севере, драконы... Они ведь тебе не друзья, правда?','','239484','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что? О нет... Нет-нет-нет! Товарищи, Расколотое Пламя вернулось! Скорее! Возвращаемся на платформу!','','239485','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бросайте все! Мы уходим. Вернемся позже. Спасибо за помощь, герои. Это вам!','','239487','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Барон никогда не найдет меня здесь, наверху. Так ведь? <Зерос нервно оглядывается.>\n\nСпасибо, $p. Ты просто спасаешь мою шкуру.\n\nКстати, а Сенегос еще с тобой? Я не хотел его оскорбить. Когда вы прибыли... Обычно я веду себя гораздо уважительнее! Особенно со столь уважаемыми драконами! Поверить не могу, что он проделал такой путь ради меня.','','239488','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Обсидиановый детеныш смотрит на вас, широко раскрыв глаза.>','','239511','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Убил бы их всех!','','239537','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но что насчет твоих зверушек-Аспектов? Они тебя предадут, Тир. Все до одного. Хочешь взглянуть?','','239538','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Черный Коготь обеспечивает нам преимущество, но внутри бушует конфликт. Приготовься.','','239583','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Приготовься, $n. Аберрий темен – внутри и снаружи.','','239584','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О! Рада снова тебя видеть.\n\n<Вопреки своим словами Патроклоза смотрит на вас настороженно.>','239586','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Здорово!','Здорово!','239589','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Еще раз! Еще раз!','Еще раз! Еще раз!','239590','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Моя любимая история...','Моя любимая история...','239591','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ур-р-ра!','Ур-р-ра!','239596','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это было круто!','Это было круто!','239597','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да! Получи!','Да! Получи!','239599','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ай да $p!','Ай да $p!','239600','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('И еще одну!','И еще одну!','239601','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мм-м... Запах напоминает мне о фруктах, которые растут только в Запретном крае.','239604','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как приятно. Спасибо тебе, $n.','239605','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Как это... утомительно... <зевает>','','239608','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кажется, у меня аллергия...','','239614','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сейчас чихну!','','239615','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В носу щекотно...','','239617','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А... а... Апчхи!','','239619','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надо же, как вкусно могут пахнуть пайки! Спасибо.','','239624','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я помогаю Амиторе тренировать других драктиров, и после этого мне всегда очень хочется есть!','','239625','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Нам всем есть чему поучиться друг у друга. Но это хорошее начало.\n\n<Маритос устал после тренировок, но все равно улыбается вам.>','','239627','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Заказ готов!','','239632','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Разведчик смотрит на вас с некоторой настороженностью, но в окружении собратьев-драктиров старается выглядеть невозмутимо.>\n\nНравится праздник? За столом достаточно места.','','239633','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ну да. Я так тебя и не поблагодарил.\n\nВ общем... спасибо. Это заставило меня задуматься о многих вещах.','','239636','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Выглядит невероятно! Гораздо лучше, чем привычные нам помои.','Выглядит невероятно! Гораздо лучше, чем привычные нам помои.','239637','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('За это нужно выпить. Выпьем за объединение драктиров!','За это нужно выпить. Выпьем за объединение драктиров!','239638','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('$n! И за тебя тоже выпьем!','$n! И за тебя тоже выпьем!','239639','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это для Целительного Крыла, что в углу.','','239640','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это для изможденного бедняги снаружи.','','239641','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это для шумных, что за столом вверх по лестнице.','','239642','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Может быть, внизу...','Может быть, внизу...','239643','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я здесь единственный дракон по имени Жуса, это уж точно...','Я здесь единственный дракон по имени Жуса, это уж точно...','239644','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Саркарет оставил на консоли частицу Хранителя клятв.','','239719','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хватит, Саркарет!','239721','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не трус, в отличие от тебя. Я приму дар, уготованный мне.','','239723','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне нужно время!','','239726','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Неважно. Ты не стоишь моего внимания, Эмберталь!','','239730','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не знаю, как у тебя вышло, но местная живность в ярости. Скорей на базу!','','239741','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Расколотое Пламя близко! Скорее! Надо расчистить руины джарадинов на западе и помочь Телину с исследованием!','','239744','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Вокруг центрального маяка вьется рой мотыльков. Из-за них мы не видим света. Лети ввысь и сбей их оттуда!','','239746','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Отличная работа, солдат! Результаты видны даже отсюда.','','239748','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Смотри, как блестят ракушки! Хурма им так обрадуется! Может, убедишь парочку улиток оставить свои домики?','','239769','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ого, когда они в слизи, то блестят еще сильнее!','','239770','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тебе повезло. Я недавно раздобыла кое-какие... зажигательные устройства. Отправлю их вам.','239806','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что удалось найти, $n?','','239809','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Прежде чем вступать в битву, надо разузнать все что можно и запастись ресурсами.','','239812','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я лично позабочусь о том, чтобы эти... эксперименты больше не сбежали.','','239818','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Годы мучений – и все напрасно!','','239824','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что ж, это должно быть интересно.\n\nСуригоса – одна из наших самых увлеченных охотниц на магов, и в своем деле она весьма хороша. В конце концов, именно она обнаружила это \"Растоптанное Знамя\".\n\nОднако наш дорогой Аспект, похоже, хочет решить этот вопрос бескровно. Полагаю, тут в дело вступаешь ты.','','239841','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не знаю, что задумал Саркарет, но вряд ли что-то хорошее.','239851','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Значит, надо все исправить и вернуть его к жизни.','','239860','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это Тир! За Тира!','','239861','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Надеюсь, ты прав!','','239867','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы никому не хотели причинить вреда. Мы просто пытались защитить себя.\n\nВозможно, мы выбрали неправильный путь.','Мы никому не хотели причинить вреда. Мы просто пытались защитить себя.\n\nВозможно, мы выбрали неправильный путь.','239875','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У тебя, наверное, была возможность меня убить. У драконов определенно была.\n\nНо я жив.\n\nЯ этого не понимаю, но спасибо тебе.','У тебя, наверное, была возможность меня убить. У драконов определенно была.\n\nНо я жива.\n\nЯ этого не понимаю, но спасибо тебе.','239876','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Учитель Ронин никогда не желал ничего подобного. Но мы не знали, что еще сделать.\n\nЕсли ты и правда хочешь проявить милосердие, пожалуйста, отпусти Тури. Она просто пыталась помочь нам покончить с прошлым.','Учитель Ронин никогда не желал ничего подобного. Но мы не знали, что еще сделать.\n\nЕсли ты и правда хочешь проявить милосердие, пожалуйста, отпусти Тури. Она просто пыталась помочь нам покончить с прошлым.','239877','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне нечего тебе сказать. То, что мы сделали, было ради памяти о тех, кого мы потеряли.','Мне нечего тебе сказать. То, что мы сделали, было ради памяти о тех, кого мы потеряли.','239878','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не надо укоризненно на меня смотреть. На моем месте так поступил бы каждый.','Не надо укоризненно на меня смотреть. На моем месте так поступил бы каждый.','239879','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Распорядительница полетов Кескос молча кивает вам, не сводя глаз с неба.','239900','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Магия, которой владеют воины стихий, гибельна, разрушительна и невероятно неряшлива. Знаю, что их имена на это намекают, но все же не думала, что можно так злоупотреблять магией. Это переходит всякие границы...\n\nА про джарадинов я молчу. Их техника насыщения, возможно, и сильная, но если они с ней продолжат, мир нескоро оправится.','239901','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Это ты! Ты всегда появляешься вовремя.\n\nВоины стихий и джарадины действуют по приказу Фиракка. Будь они сплоченнее, они бы уже захватили наши позиции. Их ритуалы навеки искалечат эту землю и ее народ, если мы не сделаем все, что в наших силах, чтобы остановить их.\n\nЯ буду поддерживать этот барьер, чтобы прислужники Фиракка не навредили тем, кого ты спасаешь. Помоги, пожалуйста, чем сможешь.','','239907','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, $p! Хорошо. Нам пригодится твоя помощь.\n\nМы, искарцы, не всесильны. Да, можем исцелять раны и сражаться с грозными врагами, но в такой битве не обойтись без опытного искателя приключений вроде тебя.','239925','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не сегодня!\n\nИм нужна наша земля? Наши ресурсы? Они хотят лишить наш мир его природы?\n\nНе сегодня!','','239938','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мне понадобится копье побольше.','','239945','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Чем я могу помочь, |3-6($c)?','Чем я могу помочь, |3-6($c)?','239971','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Проходил! Бедолага побросал свои вещи на землю. У него был очень расстроенный вид.','Да! Бедолага побросал все на землю. У него был очень расстроенный вид.','239981','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Он улетел на север, к бастионам.','Он улетел на север, к бастионам.','239982','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Красный, как чешуя королевы Алекстразы. Это символ жизни и энергии!','Красный, как чешуя королевы Алекстразы. Это символ жизни и энергии!','239983','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А сколько цветов видишь ты?','А сколько цветов видишь ты?','239984','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не смотри! Еще не готово!','Не смотри! Еще не готово!','239985','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Критики никогда меня не поймут!','Критики никогда меня не поймут!','239986','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я хочу изобразить прошлое, настоящее и будущее на одной картине!','Я хочу изобразить прошлое, настоящее и будущее на одной картине!','239987','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эта картина не должна быть на что-то похожа! Она должна пробудить в тебе эмоции!','Эта картина не должна быть на что-то похожа! Она должна пробудить в тебе эмоции!','239988','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эх... Набросок выглядел лучше...','Эх... Набросок выглядел лучше...','239989','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ну почему эта линия всегда получается кривая?','Ну почему эта линия всегда получается кривая?','239990','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Бронзовый дракончик шумно принюхивается.>\n\nУ тебя что, духи?','','240010','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не думал, что кто-то последует за мной сюда. Зачем тебе это?','','240019','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Лицо Ратейна искажает горе.>\n\nЯ даже печатку свою сломал, так ведь? Сколько я ее с собой носил...\n\nЯ ничтожество. Я недостоин быть драктиром.','','240021','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','$n, хочешь увидеть, какие плоды принес твой тяжелый труд?','240024','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Им меня не хватает? Да они даже стоять рядом со мной не захотят. Мне было наплевать на них, пока я не попал в плен. Они простили меня, хотя я никак не заслужил их прощение.\n\n<Ратейн горько смеется.>\n\nЯ видел, как они веселились, и... Я не смог. Мне тут не место.','','240025','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Оставь меня в покое... А!','','240027','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не думал, что меня кто-нибудь здесь найдет – тем более ты.','','240028','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не могу выразить словами, как я счастлива, что провела с вами время.\n\nНо что касается дела, из-за которого вы здесь...\n\n<Киригоса грустно улыбается.>','240044','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Все можно исправить.','','240063','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Ратейн вздыхает.>','','240064','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Верно. Может, я и не заслуживаю такой доброты, но... Я могу это исправить.','','240065','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я могу быть рядом со всеми. Я могу заслужить место среди них.','','240066','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Спасибо. Кто еще стал бы меня искать?','','240067','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я слышал о Пиратской Бухте прелюбопытные вещи. Если у тебя были какие-то дела с местными гоблинами или еще кем-нибудь, лучше это скрыть. Так нам будет проще выполнить нашу спасательную операцию.','','240112','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не хочу хвастаться, но всем известно, что воины Черной Чешуи – лучшие бойцы. Радуйся, что мы на твоей стороне.\n\n<Амитора подмигивает вам.>\n\nВпрочем, тебе удалось перенять у бескрылых кое-какие приемы. Дай знать, если захочешь испытать их в деле.','240152','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как приятно снова сражаться ради удовольствия!','240154','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Освойся тут немного. Как подготовишься, скажи.','240157','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вот как это делается, $n!','240158','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Подожди... Дай переведу дух...','240159','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты здесь! Как раз вовремя. То, что мне нужно, у тебя? Я очень жду!','','240161','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись в журнале номер один. \n\nПримечание автора: надо добавить введение, в котором подробно описывается кража кристального средоточия из хранилища Нексуса. \n\nПоследствия той битвы между Шандаралом и синими драконами ощущаются до сих пор – во всех частицах пронизанной магией вечной мерзлоты, покрывающей оскверненные руины этого города. Даже сейчас в руинах слышен скорбный гвалт духов, снова и снова переживающих свою кончину.\n\nМоя работа преследует следующие цели: во-первых, установить причину возникновения волшебной аномалии, которая привязала духов к месту их смерти; во-вторых, определить эффективные средства, которые позволят устранить эту аномалию; и, наконец, провести необходимый ритуал и убедиться, что призраки обрели покой.','240162','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись в журнале номер сорок пять.\n\nПервые попытки нейтрализовать аномалию с помощью базовых способов разрушения чар не увенчались успехом. Любопытный факт: похоже, аномалия устойчива ко всем заклинаниям, которые накладываю я. Чары словно хранят память о тех, кого они пропитали, и усиливают свое действие против существ, продвижение которых они были призваны остановить.\n\nИными словами, боюсь, что эти чары, пускай и случайным образом, стали надежнейшей защитой от вмешательства синих драконов. Это и чрезвычайно иронично, и весьма проблематично для продолжения моего исследования. Придется изучить другие возможности.','240163','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись в журнале номер сто девяносто девять.\n\nЯ не помню, когда в последний раз разговаривала с другим живым существом. Судя по перемещению лун по небу, я прибыла в лес Хрустальной Песни около года назад. Успехов достигла незначительных. \n\nОсколки кристального средоточия, с помощью которого, как я понимаю, наложили эти чары, раскиданы по земле, по которой я хожу. Но когда я хочу подобрать их, они ускользают, избегая моего прикосновения. Будто хранят в себе обиду Шандарала на мой род.\n\nИ все же я не отступлю. Синие драконы создали эту проблему – синий дракон ее решит. Жаль только, что не с кем поговорить в процессе.\n\nС кем-нибудь... помимо парящей книги.','240164','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Запись в журнале номер двести тридцать... что-то там. Четыре. Два-три-четыре.\n\nЯ устала. Плач и стоны отголосков Шандарала звучат в моей голове, как хор проклятых. Они кричат от страха с пугающе часто. Цикл повторяется снова и снова. Никаких вариантов, никакого облегчения.\n\nЯ в тупике. Может, все это и впрямь необратимо.','240165','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','<Парящая перед вами книга оказывается сборником записей исследователя. Неподалеку парит перо, готовое написать новые слова на чистой странице. В книгу наспех вложено несколько закладок в разных местах.>','240166','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Бей сильнее! Используй крылья!','240196','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','$n! Хочешь сразиться?','240197','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Полегче, Сабеллиан. Ты быстро приходишь в себя, но рана была тяжелой.','','240201','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я в порядке.','','240202','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Саркарет.\n\nЯ ищу его с тех пор, как он в прошлый раз встал у меня на пути. Подозреваю, что он и в этот раз не откажется от своих безумных идей, но я пока еще не потеряла надежду его исправить.\n\nНас осталось совсем мало.','240222','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Выдвигаемся. Нельзя терять времени.','','240225','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я как-то поднес напиток рыцарю смерти. Кружка покрылась льдом... прямо у него в руках!','','240306','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Олаф танцует под музыку, радуя фанатов и не обращая никакого внимания на раздраженных посетителей.>','','240307','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Саркарет, хоть и ненадолго, стал владельцем Хранителя клятв. Возможно, это наложило на него определенный отпечаток, или он до сих пор хранит частицу перчатки. Теперь Аберрий принял его как своего хозяина.\n\nНам это не сулит ничего хорошего. Совсем ничего.','','240335','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эти драконоиды тысячи лет хранили верность мертвому дракону. Меня восхищают их стойкость и непоколебимость. Молюсь, чтобы у нас получилось их переубедить.','','240336','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Наконец-то мы перестали спорить и начали работать вместе. Как настоящие союзники. Надеюсь, в будущем мы сохраним это единство.','','240337','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Приятно все-таки сбросить напряжение мечом и пламенем.','','240338','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не в первый раз поднимаю оружие против обманутых слуг злодеев-господ.','','240340','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Гневион – предсказуемый противник и легко поддается влиянию. Он как открытая книга.\nВ Абиссиане видно искреннее желание служить на благо черных драконов... но в его альтруизм тяжело поверить.','','240341','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('<Бейлог крепко спит.>','','240399','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы многого не знаем о пещере Заралек.','','240421','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Когда джарадины нашли эту пещеру, они обнаружили там дракозмеев.\n\nПроследив за джарадинами, мы увидели, что на них устроили засаду неизвестные дракониды. Сабеллиан утверждает, что они несли знамя Нелтариона. Выходит, они все еще ему верны.','','240434','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Драконы Бесконечности проникли во временные пути. Мы должны принять меры.','','240558','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вы только оттягиваете неизбежное! Дорнозму не остановить!','240559','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мы будем сдерживать их подкрепления, а ты займись их командиром.','','240560','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да! Мы тебя прикроем!','240561','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Открываю временные пути.','','240562','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Обожаю это дело!','240563','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну и ну. Пути времени перепутаны.','240564','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Враги постарались замести свои следы. На это уйдет какое-то время...','','240565','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Защитники – уничтожайте все возникающие аномалии. Нельзя дать им еще сильнее исказить пути времени.','','240566','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как ты думаешь, в какое время они направились?','240567','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не знаю. Они уже не раз пытались изменить основную ветку, но вторжение в Бронзовый храм?','','240568','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Значит, они в далеком прошлом. Они хотят переписать всю историю!','240569','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Именно так. И они не успокоятся, пока не призовут моего темного двойника... Дорнозму.','','240570','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Даже Аспект Времени не должен менять временные пути!','240571','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет! Мы пытаемся их починить!','240573','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('У нас нет выбора. Защитники, сражайтесь. Она не должна нас остановить.','','240574','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Бесконечные там – в том времени – куда я не смею идти.','','240575','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты с нами не пойдешь?','240576','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('В каком-то смысле я буду с вами. Но не в физическом облике.','','240577','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я в вас верю, друзья. И даже если Дорнозму восстанет, знайте, что не все потеряно.','','240578','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тело над нами, это... Это Галакронд! Он только что пал.','240579','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Осторожно! Мы вернулись в то время, когда Аспекты еще не были таковыми.','240580','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нам надо найти... э-э... Прото-аспектов? Они скажут нам, что делать!','240581','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ой-ой. Слизь заряжена некротической энергией!','240586','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Придется с этим разобраться!','240587','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ноздорму! Алекстраза! И остальные! Драконы Бесконечности здесь, их надо остановить.','240588','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Они хотят... ох, нет времени объяснять!','240589','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пожалуйста, просто поверьте. От этого зависит будущее драконов.','240590','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты странный маленький дракон. Но я чувствую, что у тебя доброе сердце.','240591','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не могу объяснить, но... я чувствую, что ей можно верить.','','240592','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я чую странную энергию внизу и могучую силу земли наверху. И что-то еще...','','240594','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Земли?.. О, нет. Э-э... мы этим займемся, я и эти герои. А вы разберитесь с Бесконечными!','240595','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хорошо, маленький дракон. Друзья мои, за мной!','240596','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Если Иридикрон пришел вместе с Бесконечными, они, должно быть, заодно. Это плохо, очень плохо!','240597','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Он вытягивает сущность Галакронда... в сосуд?','240598','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Голод прошлых столетий. Я верну его!','','240599','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Где мы... В каком времени? Нет... т-тут что-то не так. Бесконечные изменили основную ветку!','240600','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Этого не может быть. Если Дорнозму существует, значит, Ноздорму...','240601','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет. Нет, мы это исправим. Надо вернуться во временные пути!','240602','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тир сражается за Бесконечных? Этого не может быть!','240603','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слишком долго я был скован узами титанов. Теперь мой разум открыт бесконечным возможностям...','','240604','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тир всегда был героем для драконов. Мне приходится напоминать себе, что на самом деле это был не он...','240606','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ладно, давайте это исправим. Надо просто... ой.','240607','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тьфу ты! Пути времени запутались еще сильнее.','240615','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Так я не смогу вернуть нас в прошлое.','240618','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Когда время переписывают, тут начинается бедлам. Можно ждать чего угодно!','240619','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нам надо зачистить аномалии прежде, чем мы вернемся в прошлое.','240622','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, нет. Это Морхи! Что она тут делает?','240623','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Доброхоточка пытается спасти Ноздорму? Ничего не выйдет!','240624','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','С тобой я разберусь сама! В этой временной линии герои не нужны.','240625','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет! Это должно было быть... мое время...','240626','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вы все знаете... что делать...','240627','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Бедная Морхи. Может, в другое время мы могли бы стать друзьями.','240628','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','А это что? Временная реликвия? Две стрелы указывают направо... Хм...','240629','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Осталась еще одна аномалия! Пошли в другую сторону.','240630','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Теперь мы можем открыть путь в прошлое. Пошли! Надо спасти Ноздорму.','240631','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Орда и Альянс сражаются? Ну, это может быть любое время.','240632','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У нас есть только одна попытка. Чтобы вернуться так далеко в прошлое, надо много энергии. А я не Аспект.','240633','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну, поехали.','240634','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Отлично! Надо убедиться, что Дорнозму не... появится? Или не восстанет?','240635','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну, вы меня поняли.','240636','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У нас нет на это времени! Где та реликвия, которую я взяла?','240637','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ага! Ну, была не была!','240638','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Фу ты. Справитесь с этим?','240639','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пошли! Аспекты впереди. Ну, то есть... прото-аспекты.','240640','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вы все! Драконы Бесконечности здесь, их надо остановить. Поверьте мне!','240641','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты странный маленький дракон. Но я чувствую, что у тебя доброе сердце.','240642','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Не могу объяснить, но... я чувствую, что ей можно верить.','','240643','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, да, я знаю.','240644','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','На этот раз мы все должны остановить Бесконечных. Это черные светящиеся драконы в расщелине.','240645','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','На этот раз?','240646','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это долгая история. Просто поверь мне.','240647','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я знаю, что наверху есть большой дракон, но с ним мы разберемся после Дейоса.','240648','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хорошо, маленький дракон. Друзья мои! За мной! Сразимся вместе!','240649','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Дорнозму восстанет и приведет нас в лучшее будущее! Разве вы не видите?','','240650','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, мы видели достаточно! Не в этот раз, Дейос.','240651','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Прости, я знаю, у тебя куча вопросов. Но временная линия...','240652','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Кажется, я понимаю. Надо сохранить будущее, и это знание может его изменить.','','240653','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тогда они должны вернуться.','240654','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Должны. Но все же... Будущее, в которое ты вернешься... За него стоит сражаться?','','240655','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, да, Ноздорму. Безусловно. Ты его еще увидишь.','240656','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эти события заставили меня задуматься... Что нас ждет впереди?','','240657','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Даже представить себе не могу.','','240658','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Что бы там ни было, я клянусь, что буду защищать род драконов.','','240659','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, друзья мои. Вместе мы защитим жизнь в этом мире.','240660','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нашу связь ничто не разорвет. Ни теперь, ни в будущем.','240661','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я не рассчитываю на то, что Сабеллиан и Гневион станут лучшими друзьями, но мне приятно видеть, что они действуют сообща. Скверна, паранойя и хаос пожирали мой род тысячи лет. Сейчас нам как никогда важно единство.\n\nТвоя помощь не осталась незамеченной. Теперь у рода черных драконов есть надежда на будущее – благодаря тебе.\n\nСпасибо.','','240720','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я хотел бы насладиться этой победой, но не могу.\n\nСрок, которого я боялся, приближается, и я никак не могу это предотвратить. Можно перевернуть песочные часы, чтобы песок сыпался иначе, но это лишь отсрочит неизбежное. Не отменит.\n\nКогда пробьет мой час, ты сделаешь то, что должно. Я знаю.\n\nЯ благодарю тебя сейчас, пока еще могу это сделать.','','240721','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Возможно, до покоя нам еще далеко, но впервые с тех пор, как воплощения стихий вырвались на свободу, в моем сердце теплится надежда, что у нас все получится.\n\nМоя семья не ограничивается моим родом. Аспекты и их сородичи – тоже ее часть. Мы уже давно не собирались вместе вот так. Я... скучал по этому.\n\nСпасибо! За всю твою помощь. Если это до сих пор не очевидно, то я и тебя считаю частью своей семьи.','','240722','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Рада, что ты здесь.\n\nУ нас тут творится сущий хаос с временными потоками. И неудивительно: если забыть об осторожности, пока по ним путешествуешь, можно и не такое натворить. Вот-вот появится портал времени. Буквально чувствую его.\n\nНо я этого так не оставлю! Надо все исправить, и ты нам поможешь.','240775','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я бронзовый дракон, а значит, могу провести тебя сквозь время! Ты сможешь изучить историю Азерота!\n\nПока ты участвуешь в кампаниях прошлого, временная линия подстраивается под твой уровень. Так будет гораздо интереснее и приключения тебе не наскучат. \n\nПросто скажи, в какую временную линию хочешь отправиться!','240784','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','<Хроми цокает языком и внимательно вас осматривает.>\n\nТочно? \n\nТам целый мир, огромный, гигантский! В нем страх как легко заблудиться. \n\nМожет, у тебя найдется друг, который поможет сориентироваться? Советую отыскать такого. \n\nА еще мне придется искривить потоки времени, и тебя, скорее всего, отрежет от того, кто провожает тебя сейчас!','240788','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','В таком случае ты всегда сможешь перескочить в другую временную линию! Весь Азерот будет подстраиваться под твой уровень, так что исследуй все, что душе угодно!\n\nА когда наберешься достаточно опыта, чтобы столкнуться с испытаниями настоящего, то просто покинешь кампанию.\n\nЯ буду ждать здесь. Обращайся, если решишь оставить временную линию или выбрать какую-то другую.','240841','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ну конечно! Отправляй своих друзей ко мне, и я проведу их в твою кампанию прошлого.\n\nИначе твой друг попросту не увидит тебя: вы окажетесь в разных временных линиях!','240843','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Конечно! Всегда с радостью жду твоих вопросов! Если только они уже не прозвучали... ну, в прошлом. Тогда я просто их помню! Отлично помню.\n\nНу... на какой ответить?','240872','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хороший вопрос! Их так много, что выбрать одну и впрямь непросто!\n\nХм-м, дай подумать... А знаешь что? Опиши-ка свое идеальное приключение.','240875','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, не волнуйся. Мы придумаем, как их остановить!','240961','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Он был самым большим протодраконом. В своем голоде он чуть не сожрал всех остальных!','240963','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это похоже на Душу Дракона, но только еще древнее.','240965','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Но ваш путь закончится здесь!','','240966','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','У нее были шикарные волосы. Но с макияжем перебор.','240967','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Что это за кошмарное существо перед нами? И что за реликвия у него в руках?','240968','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','О, тебя оно еще долго не побеспокоит. А вот нас... ох, елки.','240969','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','До свидания все! Была рада еще раз с вами встретиться... в первый раз!','240970','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Прекрасный выбор! Азерот – отличное место для новичка! \n\nПравда, если хочешь осмотреть все целиком, придется остановить Смертокрыла и не дать ему разрушить мир!\n\nОн уничтожал целые земли, знаешь ли. Там страх что творится!','240980','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Немного любви, и эти камни явят миру свою истинную красоту!','240987','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я считаю себя экспертом в этом вопросе.','240988','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вот здесь огранку поправить, отполировать немного и...','240989','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Только посмотри! Этому блеску позавидуют даже самые яркие кристаллы пещеры! Если, конечно, кристаллы умеют завидовать.','240990','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Такие богатые оттенки... Говорят, на поверхности есть драконы – разноцветные, как драгоценные камни!','240991','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Думаю, однажды мне все-таки стоит туда выбраться и увидеть все это самой!','240993','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Как насчет планеты, плывущей посреди Круговерти Пустоты? Покрытой бесплодными пустошами, неприступными горами и широкими равнинами?\n\nТогда тебе в Запределье!\n\nИмей в виду: там живет полным-полно диких и агрессивных существ, так что готовься к битве! То еще местечко!','240995','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тогда тебе в Нордскол! Ужасный Король-лич оскверняет цикл жизни и смерти и возрождает павших героев, а потом ставит их в бесконечные ряды Плети.\n\nНельзя, чтобы она захватила весь Азерот! Можешь представить меня нежитью? Вот и я не могу! Это ж никакого веселья!','240996','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Значит, тебе в Пандарию!\n\nТам растут густые леса, высятся горы до небес и Орда опять ведет войну с Альянсом. Будет непросто, зато узнаешь, чего стоишь и что значит – быть собой.\n\nА если устанешь сражаться, отправляйся на ферму Солнечной Песни. Фермер Юн никогда не откажется от помощи.','240997','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хм-м, поняла!\n\nОрда и Альянс отправились в Дренор, чтобы не пустить Легион в наш мир.\n\nИм понадобятся герои вроде тебя... если, конечно, не испугаешься!','241000','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','$cs основали свой орден, чтобы дать отпор Пылающему Легиону. Не желаешь присоединиться к ним?\n\nБудешь сражать демонические орды самым великолепным и безумным оружием для |3-8($c), каким еще ни один из вас не мог похвастаться. Ну, до вашего ордена. Что может быть лучше, а?','241001','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Уверена, обитатели Темных Земель тебе обрадуются!\n\nИм нужна помощь: нельзя, чтобы Тюремщик перекроил мир под себя!','241002','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Армия Бесконечных нас задержит.','241027','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пора ускориться!','241028','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Основная ветка моя!','241091','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Слуги титанов пришли сразиться со мной.','','241097','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Да, да! Садись на дилижанс моей госпожи, и мы отправимся в путь!','','241156','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я возвышу тебя над остальными. Ты этого заслуживаешь.','','241162','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Было ли это случайностью? Или твоим возможностям просто завидуют?','','241163','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты отличаешься от остальных. В тебе есть... нечто особенное.','','241164','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты можешь избежать этой участи... нужно лишь слушать меня.','','241165','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Они меня даже не слышат. Это ли не доказательство твоей исключительности?','','241166','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Только я вижу, как ты борешься. Только я понимаю твою боль.','','241167','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сосредоточься на моем голосе. Пусть все лишнее исчезнет...','','241168','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Сколько же от них шума... Пусть твой путь к славе пролегает в тишине.','','241169','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Мой выбор. Мой герой.','','241171','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ха! Вы все поняли, да? Но вы опоздали!','241179','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Меня зовут Морхи, чучело. И да! Пришло время призвать Дорнозму!','241180','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хочешь меня остановить? Поймай меня, если сможешь.','241181','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Ты работаешь на Бесконечных? Какая ужасная Хроми!','241182','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Да, ты можешь меня допросить. А можешь пойти и спасти свой драгоценный Бронзовый Храм.','241183','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Храм? Что вы такое задумали?','241184','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вознесение нашего повелителя Дорнозму! И чем дольше ты тут со мной болтаешь...','241185','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Морхи злобно смеется.','241186','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нам надо идти. Ноздорму нужна наша помощь! Если он станет Дорнозму, то... Нельзя это допустить!','241187','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Мы здесь, Ноздорму!','241188','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нет, никогда не поздно! Мы еще можем их остановить. Мы тебя не оставим.','241189','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Боюсь, для меня уже поздно. Драконы Бесконечности уже проникли в храм.','','241190','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты всегда веришь в лучшее, Хронорму. Да, мы должны попытаться.','','241191','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Эй ты, не спишь?','','241193','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Враги угрожают нам и сверху, и снизу.','241194','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Долго же тебя было не добудиться!','','241195','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Значит, мы в ловушке.','241196','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Я бы так не сказал.','','241197','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ты, должно быть, нечасто отдыхаешь? Мир давит на тебя тяжким бременем...','','241198','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Я вам помогу.','241199','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Разведайте, что впереди. Я вас догоню.','241200','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('А вот моей госпоже это чувство незнакомо. Она свободна, как сама ночь, и нерушима, как луна в небе.','','241201','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Ее сила – никакое не бремя. Это дар, которым она охотно делится. Да-да!','','241202','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('Она слышала о твоих подвигах, |3-6($r). И просит твоей помощи в обмен на... кое-какие дары.','','241203','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Один есть!','241205','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Пока-пока!','241206','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Не повезло тебе. Я позабочусь о временных путях!','241207','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Временная бомба пошла!','241208','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Глотай песок времени!','241209','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Много Морхи лучше, чем одна!','241210','50401');
