insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Хорошо, что ты здесь, $n. Нужно закрыть разлом во времени.','241481','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Эти существа попали сюда из другой временной линии.','241482','50401');
