insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Тут повсюду мурлоки! Прогоните их.','241491','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сюда надвигаются машины. Остановим их!','241492','50401');
