insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сюда нагрянули враги из далекого будущего. Защитим наше время.','241494','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Из какого кошмара лезут эти твари? Не дайте им победить!','241495','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Сражайтесь с этими незваными гостями!','241496','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Защитим наше время от врагов! Плечом к плечу!','241497','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Перед нами могучий противник. Одолейте его!','241498','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Это безумие вызвано хаотичным порождением темпоральной энергии. Приготовьтесь к бою!','241499','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Вот источник наших проблем: расколотая причинность. Избавимся от нее!','241500','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Враг строит укрепления. Уничтожим их!','241501','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Враг устанавливает здесь укрепление. Снесем его!','241502','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Чтобы защитить наше время, нужны укрепления. Постройте их!','241503','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Постройте несколько сооружений. Действуем дружно!','241504','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Нам нужны укрепления. Построим их вместе!','241505','50401');
insert into `broadcast_text` (`Text`, `Text1`, `ID`, `VerifiedBuild`) values('','Помогите нашим союзникам в бою.','241506','50401');
